INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Sukzessionsfläche','''Sukzessionsfläche'' ist eine Fläche, die dauerhaft aus der landwirtschaftlichen oder sonstigen bisherigen Nutzung herausgenommen ist und die in den Urzustand z. B. Gehölz, Moor, Heide übergeht.','funktion','ax_unlandvegetationsloseflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Gewässerbegleitfläche','''Gewässerbegleitfläche'' bezeichnet eine bebaute oder unbebaute Fläche, die einem Fließgewässer zugeordnet wird. Die Gewässerbegleitfläche ist nicht Bestandteil der Gewässerfläche.','funktion','ax_unlandvegetationsloseflaeche');
