INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('8710','Küstengewässer','''Küstengewässer'' ist die Fläche zwischen der Küstenlinie bei mittlerem Hochwasser oder der seewärtigen Begrenzung der oberirdischen Gewässer und der seewärtigen Begrenzung des deutschen Hoheitsgebietes. Dem mittleren Hochwasser ist der mittlere Wasserstand der Ostsee gleichzusetzen.','funktion','ax_meer');
