INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1653','Naturdenkmal','''Naturdenkmal'' ist eine rechtsverbindlich festgesetzte Einzelschöpfung der Natur, deren besonderer Schutz erforderlich ist (z.B. Baum).','artderfestlegung','ax_naturumweltoderbodenschutzrecht');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1624','Naturpark','''Naturpark'' ist ein einheitlich zu entwickelndes und zu pflegendes Gebiet, das großräumig ist, überwiegend Landschaftsschutzgebiet oder Naturschutzgebiet ist, sich wegen seiner landschaftlichen Voraussetzungen für die Erholung besonders eignet und nach den Grundsätzen und Zielen der Raumordnung und der Landesplanung für die Erholung oder den Fremdenverkehr vorgesehen ist.','artderfestlegung','ax_naturumweltoderbodenschutzrecht');
