INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1622','Geschützter Landschaftsbestandteil','''Geschützter Landschaftsbestandteil'' ist ein rechtsverbindlich festgesetzter Teil von Natur und Landschaft, dessen besonderer Schutz zur Sicherstellung der Leistungsfähigkeit des Naturhaushalts, zur Belebung, Gliederung oder Pflege des Orts- und Landschaftsbildes oder zur Abwehr schädlicher Einwirkungen erforderlich ist.','artderfestlegung','ax_naturumweltoderbodenschutzrecht');
