INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1611','Flora-Fauna-Habitat-Gebiet','''Flora-Fauna-Habitat-Gebiet'' ist ein Schutzgebiet von gemeinschaftlicher Bedeutung im Sinne der Richtlinie 92/43/EWG des Rates der Europäischen Wirtschaftsgemeinschaft zur Erhaltung der natürlichen Lebensräume sowie der wildlebenden Pflanzen und Tiere.','artderfestlegung','ax_naturumweltoderbodenschutzrecht');
