INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3002','Verkehrsführung','''Verkehrsführung'' bedeutet, dass auf ''Damm, Wall, Deich'' ein Verkehrsweg verläuft.','funktion','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3003','Hochwasserschutz, Sturmflutschutz zugleich Verkehrsführung','''Hochwasserschutz, Sturmflutschutz zugleich Verkehrsführung'' bedeutet, dass ''Damm, Wall, Deich'' dem Schutz vor Hochwasser dient und auf dem gleichzeitig ein Verkehrsweg verläuft.','funktion','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3001','Hochwasserschutz, Sturmflutschutz','''Hochwasserschutz, Sturmflutschutz'' bedeutet, dass ''Damm, Wall, Deich'' dem Schutz vor Hochwasser bzw. Sturmflut dient.','funktion','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3004','Lärmschutz','''Lärmschutz'' bedeutet, dass ''Damm, Wall, Deich'' dem Schutz vor Lärmemissionen dient.','funktion','ax_dammwalldeich');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','Length','HHO','''Objekthöhe'' ist die maximale Höhendifferenz in [m] zwischen dem höchsten Punkt von ''Damm, Wall, Deich'' und der Geländeoberfläche.','ax_dammwalldeich','DLKM','objekthoehe');
