INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1960','Schlafdeich','''Schlafdeich'' ist ein ehemaliger Hauptdeich, der infolge einer Vorverlegung der Deichlinie zu einem Binnendeich geworden ist und keine unmittelbare Schutzaufgabe mehr zu erfüllen hat.','art','ax_dammwalldeich');
