INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1980','Binnendeich','''Binnendeich'' ist ein Deich an kleineren Flüssen, der Überschwemmungen durch ablaufendes Oberflächenwasser verhindern soll.','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2003','Knickmitte','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1910','Hochwasserdeich','''Hochwasserdeich'' ist ein Deich an einem Fliessgewässer oder im Küstengebiet, der dem Schutz eines Gebietes vor Hochwasser oder gegen Sturmfluten dient.','art','ax_dammwalldeich');
