INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1970','Mitteldeich','''Mitteldeich'' ist ein Deich der 2. Deichlinie, auch an größeren Flüssen. Er soll Überschwemmungen beim Bruch des Deiches der ersten Deichlinie verhindern.','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1930','Überlaufdeich','''Überlaufdeich'' ist ein Deich vor dem Hauptdeich, der in erster Linie dem Schutz landwirtschaftlich genutzter Flächen gegen leichte Sturmtiden dient und der bei höheren Sturmtiden überströmt wird.','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1990','Wall','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2000','Knick','''Knick'' ist ein bewachsener Erdwall, der als Einfriedung und zum Windschutz von Wiesen und Äckern dient.','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1950','Polderdeich','''Polderdeich'' ist ein vor dem Hauptdeich liegender Deich, der landwirtschaftlich nutzbares Land (z. B. Marschland) schützt.','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1940','Leitdeich','''Leitdeich'' ist ein dammartiges Bauwerk im Watt, um strömendes Wasser in bestimmte Richtungen zu lenken und zum Schutz von Wasserläufen im Watt (Außentiefs) vor Versandung.','art','ax_dammwalldeich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1920','Hauptdeich, Landesschutzdeich','''Hauptdeich, Landesschutzdeich'' ist ein Deich der ersten Deichlinie zum Schutz der Küsten- und Inselgebiete gegen Sturmflut.','art','ax_dammwalldeich');
