INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1110','Pferderennsport','''Pferderennsport'' bedeutet, dass eine Rennbahn zur Ausübung des Pferderennsports genutzt wird.','sportart','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1020','Leichtathletik','''Leichtathletik'' bedeutet, dass ein Spielfeld oder Stadion zur Ausübung verschiedener Leichtathletikdisziplinen genutzt wird.','sportart','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1040','Reiten','''Reiten'' bedeutet, dass ein Stadion oder eine Rennbahn zur Ausübung des Reitsports genutzt wird.','sportart','ax_bauwerkoderanlagefuersportfreizeitunderholung');
