INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1431','Zuschauertribüne, überdacht','''Zuschauertribüne, überdacht'' bedeutet, dass ''Zuschauertribüne'' mit einer Dachfläche ausgestattet ist.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1510','Wildgehege','''Wildgehege'' ist ein eingezäuntes Areal, in dem Wild waidgerecht betreut wird oder beobachtet werden kann.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('9999','Sonstiges','''Sonstiges'' bedeutet, dass die Bauwerkfunktion bekannt, aber in der Attributwertliste nicht aufgeführt ist.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
