INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1410','Spielfeld','''Spielfeld'' ist eine abgegrenzte, markierte Fläche, auf der die Sportart unmittelbar ausgeübt wird, z.B. die einzelnen Fußballfelder (Hauptplatz und Trainingsplätze) einer größeren Anlage. Die zusammenhängenden Spielflächen innerhalb einer Tennisanlage werden zu einem Spielfeld zusammengefasst','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1440','Stadion','''Stadion'' ist ein Bauwerk mit Tribünen und entsprechenden Einrichtungen zur Ausübung von bestimmten Sportarten.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1470','Sprungschanze (Anlauf)','''Sprungschanze (Anlauf)'' ist eine Anlage zum Skispringen mit einer stark abschüssigen, in einem Absprungtisch endenden Bahn zum Anlauf nehmen.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1411','Hartplatz','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1430','Zuschauertribüne','''Zuschauertribüne'' ist ein großes Gerüst oder ein festes, meist überdachtes Bauwerk mit ansteigenden Sitzoder Stehplatzreihen für Zuschauer.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1432','Zuschauertribüne, nicht überdacht','''Zuschauertribüne, nicht überdacht'' bedeutet, dass die Zuschauertribüne keine Dachfläche besitzt.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1480','Schießanlage','''Schießanlage'' ist eine Anlage mit Schießbahnen für Schießübungen oder sportliche Wettbewerbe.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1490','Gradierwerk','''Gradierwerk'' ist ein mit Reisig bedecktes Gerüst, über das Sole rieselt, die durch erhöhte Verdunstung konzentriert wird.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1420','Rennbahn, Laufbahn, Geläuf','''Rennbahn, Laufbahn, Geläuf'' ist eine je nach Art des Rennens verschiedenartig gestaltete Strecke (oval, gerade, kurvig), auf der das Rennen stattfindet.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1450','Schwimmbecken','''Schwimmbecken'' ist ein mit Wasser gefülltes Becken zum Schwimmen oder Baden.','bauwerksfunktion','ax_bauwerkoderanlagefuersportfreizeitunderholung');
