INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1610','Quelle','''Quelle'' ist eine natürliche, örtlich begrenzte Austrittsstelle von Wasser.','art','ax_gewaessermerkmal');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1650','Watt','''Watt'' ist ein aus Sand oder Schlick bestehender Boden an flachen Gezeitenküsten und in Mündungstrichtern von Flüssen, der bei Ebbe ganz oder teilweise trocken fällt und bei Flut vom sogenannten Wattenmeer überspült wird.','art','ax_gewaessermerkmal');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('9999','Sonstiges','art','ax_gewaessermerkmal');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1660','Priel','''Priel'' ist eine natürliche Rinne im Watt, die auch bei Ebbe Wasser führt.','art','ax_gewaessermerkmal');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1640','Sandbank','''Sandbank'' ist eine vegetationslose Sand- oder Kiesablagerung auf dem Meeresboden oder in Flüssen, die durch Brandung oder Strömung aufgebaut wird.','art','ax_gewaessermerkmal');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1630','Stromschnelle','''Stromschnelle'' ist eine Flussstrecke mit höherer Strömungsgeschwindigkeit durch ein besonders starkes Gefälle sowie oft auch geringerer Wassertiefe.','art','ax_gewaessermerkmal');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1620','Wasserfall','''Wasserfall'' ist ein senkrechter oder nahezu senkrechter Absturz eines Wasserlaufs, der über eine oder mehrere natürliche Stufen verlaufen kann.','art','ax_gewaessermerkmal');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NAM','''Name'' ist der Eigenname von ''Gewässermerkmal''.','ax_gewaessermerkmal','DLKM','name');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BEZ','''Bezeichnung'' ist die von einer Fachstelle vergebene Kennziffer von ''Gewässermerkmal''.','ax_gewaessermerkmal','DLKM','bezeichnung');
INSERT INTO alkis_elemente(name,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_leitung','51005','REO','[E] ''Leitung'' ist eine aus Drähten oder Fasern hergestellte Leitung zum Transport von elektrischer Energie und zur Übertragung von elektrischen Signalen.','{"ax_bauwerkeeinrichtungenundsonstigeangaben","au_kontinuierlicheslinienobjekt"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','Integer','SPG','''Spannungsebene'' beschreibt den höchsten vorkommenden Wert in [KV].','ax_leitung','DLKM','spannungsebene');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','AX_Bauwerksfunktion_Leitung','BWF','''Bauwerksfunktion'' beschreibt die Art oder Funktion von ''Leitung''.','ax_leitung','DLKM','bauwerksfunktion');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1110','Freileitung','''Freileitung'' ist eine aus einem oder mehreren Drähten oder Fasern hergestellte oberirdische Leitung zum Transport von elektrischer Energie und zur Übertragung von elektrischen Signalen.','bauwerksfunktion','ax_leitung');
