INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2300','Moorlandschaft','''Moorlandschaft'' ist eine durch Moore geprägte Landschaft.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2400','Heidelandschaft','''Heidelandschaft'' ist eine waldfreie Landschaft der unteren Höhenstufen, die von einer mehr oder weniger lockeren Zwergstrauchformation geprägt wird.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2100','Seenlandschaft','''Seenlandschaft'' ist eine durch zahlreiche, nahe beieinander liegende Binnenseen geprägte Landschaft.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Gebirge','''Gebirge'' bezeichnet eine zusammenhängende größere Erhebung der Erdoberfläche. Es besteht aus einzelnen Bergen und Hochflächen, die durch Täler und Senken gegliedert sind.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2200','Siedlungslandschaft','''Siedlungslandschaft'' ist eine durch Siedlungen geprägte Landschaft.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1500','Tiefebene','''Tiefebene'' bezeichnet einen Teil der Erdoberfläche mit fehlenden oder kaum wahrnehmbaren Höhenunterschieden in einer Höhenlage bis etwa 200 m über NN.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2500','Wattlandschaft','''Wattlandschaft'' ist ein ebener, im Wirkungsbereich der Gezeiten liegender Küstenstreifen, der bei Hochwasser überflutet wird, bei Niedrigwasser jedoch trocken fällt, wobei das Wasser durch oft tiefe Furchen (Priele) abfließt.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2000','Inselgruppe','''Inselgruppe'' ist eine Gruppe mehrerer nahe beieinander liegender Inseln geologisch gleicher Entstehung.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Berg','''Berg'' bezeichnet eine über die Umgebung deutlich herausragende Geländeerhebung, einzeln oder als Teil eines Gebirges.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1600','Plateau/Hochebene','''Plateau/Hochebene'', bezeichnet einen Teil der Erdoberfläche mit fehlenden oder kaum wahrnehmbaren Höhenunterschieden in einer Höhenlage ab etwa 200 m über NN.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1300','Niederung/Senke','''Niederung/Senke'' bezeichnet ein tief liegendes Land an Flussläufen und Küsten.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1400','Tal','''Tal'' bezeichnet eine langgestreckte, offene Hohlform der Erdoberfläche.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1800','Dünenlandschaft','''Dünenlandschaft'' ist eine, vom Wind gebildete, durch Sandanhäufungen geprägte Landschaft.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1700','Mündungsgebiet','''Mündungsgebiet'' bezeichnet die typische, durch Ablagerung von Schwebestoffen entstandene Landschaft im Bereich der Mündung eines fließenden Gewässers in ein anderes Binnengewässer oder in ein Meer.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1900','Waldlandschaft','''Waldlandschaft'' ist eine größere und zusammenhängende Landschaft, bestehend aus mit Forstpflanzen bestockten Flächen (Wald) sowie mit dem Wald verbundene und ihm dienende Flächen.','landschaftstyp','ax_kleinraeumigerlandschaftsteil');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','CharacterString','NAM','''Name'' ist der Eigenname von ''Kleinräumiger Landschaftsteil''.','ax_kleinraeumigerlandschaftsteil','DLKM','name');
