INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2630','Deponie (oberirdisch)','''Deponie (oberirdisch)'' bezeichnet eine Fläche, auf der oberirdisch Abfallstoffe gelagert werden.','funktion','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2520','Wasserwerk','''Wasserwerk'' bezeichnet eine Fläche mit Bauwerken und sonstigen Einrichtungen zur Gewinnung und/ oder zur Aufbereitung von (Trink-)wasser.','funktion','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1440','Handel','''Handel'' bezeichnet Anlagen mit Einzelhandels- und Dienstleistungsbetrieben, die durch einheitliche Verwaltung, auf das Einzugsgebiet abgestimmter Anbieter und durch große Parkplatzflächen geprägt sind.','funktion','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1400','Handel und Dienstleistung','''Handel und Dienstleistung'' bezeichnet eine Fläche, auf der vorwiegend Gebäude stehen, in denen Handelsund/oder Dienstleistungsbetriebe ansässig sind.','funktion','ax_industrieundgewerbeflaeche');
