INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2500','Versorgungsanlage','''Versorgungsanlage'' bezeichnet eine Fläche, auf der vorwiegend Anlagen und Gebäude zur Versorgung der Allgemeinheit mit Elektrizität, Wärme und Wasser vorhanden sind.','funktion','ax_industrieundgewerbeflaeche');
