INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1450','Ausstellung, Messe','''Ausstellung, Messe'' bezeichnet eine Fläche mit Ausstellungshallen und sonstigen Einrichtungen zur Präsentation von Warenmustern.','funktion','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2600','Entsorgung','''Entsorgung'' bezeichnet eine Fläche, auf der vorwiegend Anlagen und Gebäude zur Verwertung und Entsorgung von Abwasser und festen Abfallstoffen vorhanden sind.','funktion','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2540','Umspannstation','''Umspannstation'' bezeichnet eine Fläche mit Gebäuden und sonstigen Einrichtungen, um Strom auf eine andere Spannungsebene zu transformieren.','funktion','ax_industrieundgewerbeflaeche');
