INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('7200','Öl','''Öl'' bedeutet, dass das Kraftwerk bzw. Heizwerk die durch Verbrennung von Öl freiwerdende Energie in eine andere Energieform umwandelt.','primaerenergie','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Wasser','''Wasser'' bedeutet, dass das Kraftwerk potentielle und kinetische Energie des Wasserkreislaufs in elektrische Energie umwandelt.','primaerenergie','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('7100','Kohle','''Kohle'' bedeutet, dass das Kraftwerk bzw. Heizwerk die durch Verbrennung von Kohle freiwerdende Energie in eine andere Energieform umwandelt.','primaerenergie','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5000','Gezeiten','''Gezeiten'' bedeutet, dass das Kraftwerk die kinetische Energie der Meeresgezeiten in elektrische Energie umwandelt.','primaerenergie','ax_industrieundgewerbeflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4000','Wind','''Wind'' bedeutet, dass das Kraftwerk die Strömungsenergie des Windes in elektrische Energie umwandelt.','primaerenergie','ax_industrieundgewerbeflaeche');
