INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('7300','Gas','''Gas'' bedeutet, dass das Kraftwerk bzw. Heizwerk die durch Verbrennung von Gas freiwerdende Energie in eine andere Energieform umwandelt.','primaerenergie','ax_industrieundgewerbeflaeche');
