INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Offen','''Offen'' beschreibt die Bebauung von ''Fläche gemischter Nutzung'', die vorwiegend durch einzelstehende Gebäude charakterisiert wird.','artderbebauung','ax_flaechegemischternutzung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2000','Geschlossen','''Geschlossen'' beschreibt die Bebauung von ''Fläche gemischter Nutzung'', die vorwiegend durch zusammenhängende Gebäude charakterisiert wird. Die Gebäudeabdeckung ist in der Regel > 50 Prozent der Fläche.','artderbebauung','ax_flaechegemischternutzung');
