INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1180','Polizeiwache','''Polizeiwache'' ist eine Dienststelle der Polizei.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1130','Bibliothek','''Bibliothek'' ist eine Einrichtung, in der Bücher und Zeitschriften gesammelt, aufbewahrt und ausgeliehen werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1240','Geschäft','weiteregebaeudefunktion','ax_gebaeude');
