INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1220','Arztpraxis','''Arztpraxis'' ist die Arbeitsstätte eines Arztes.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1210','Kindergarten','''Kindergarten'' ist eine Einrichtung, in der Kinder im Vorschulalter betreut werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1150','Moschee','''Moschee'' ist ein Einrichtung, in der sich Muslime zu Gottesdiensten versammeln oder zu anderen Zwecken treffen.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1120','Museum','''Museum'' ist eine Einrichtung in der Sammlungen von (historischen) Objekten oder Reproduktionen davon ausgestellt werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Touristisches Informationszentrum','''Touristisches Informationszentrum'' ist eine Auskunftsstelle für Touristen.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1140','Kapelle','''Kapelle'' ist eine Einrichtung für (christliche) gottesdienstliche Zwecke .','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1110','Theater','''Theater'' ist eine Einrichtung, in der Bühnenstücke aufgeführt werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1190','Rettungsstelle','''Rettungsstelle'' ist eine Einrichtung zur Aufnahme, Erstbehandlung und gezielten Weiterverlegung von Patienten mit Erkrankungen und Unfällen aller Art.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1040','Kino','''Kino'' ist eine Einrichtung, in der alle Arten von Filmen bzw. Lichtspielen für ein Publikum abgespielt werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1170','Apotheke','''Apotheke'' ist ein Geschäft, in dem Arzneimittel hergestellt und verkauft werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1010','Hotel','''Hotel'' ist ein Beherbergungs- und/oder Verpflegungsbetrieb.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1050','Spielkasino','''Spielkasino'' ist eine Einrichtung, in der öffentlich zugänglich staatlich konzessioniertes Glücksspiel betrieben wird.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Bankfiliale','''Bankfiliale'' ist eine Einrichtung in der Geldgeschäfte getätigt werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1080','Toilette','''Toilette'' ist eine Einrichtung mit sanitären Vorrichtungen zum Verrichtung der Notdurft.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Zoll','''Zoll'' ist eine Einrichtung der Zollabfertigung.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1070','Parkdeck','''Parkdeck'' ist eine Fläche auf einem Gebäude, auf der Fahrzeuge abgestellt werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1090','Post','''Post'' ist eine Einrichtung, von der aus Briefe, Pakete befördert und weitere Dienstleistungen angeboten werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1060','Tiefgarage','''Tiefgarage'' ist ein Bauwerk unterhalb der Erdoberfläche, in dem Fahrzeuge abgestellt werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1020','Jugendherberge','''Jugendherberge'' ist eine zur Förderung von Jugendreisen dienende Aufenthalts- und Übernachtungsstätte.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1230','Supermarkt','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1030','Gaststätte','''Gaststätte'' ist eine Einrichtung, in der gegen Entgelt Mahlzeiten und Getränke zum sofortigen Verzehr angeboten werden.','weiteregebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','GKN','''Gebäudekennzeichen'' ist ein eindeutiges Fachkennzeichen für ein Gebäude, bestehend aus den Schlüsseln für die Gemeinde (8 Stellen), Straße (5 Stellen), die Hausnummer des Gebäudes (4 Stellen), dem Adressierungszusatz (4 Stellen) und die laufende Nummer des Gebäudes (3 Stellen). Die Stellen sind jeweils rechtsbündig zu führen. Fehlende Stellen werden mit Nullen aufgefüllt. Der Adressierungszusatz und die laufende Nummer des Nebengebäudes sind optional und werden, wenn sie nicht belegt sind, mit Unterstrich "_" gefüllt. Die Attributart wird im Rahmen der Migration aus den bisherigen Verfahrenslösungen übernommen und werden nur in ATKIS dauerhaft geführt.','ax_gebaeude','DLKM','gebaeudekennzeichen');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Zustand_Gebaeude','ZUS','''Zustand'' beschreibt die Beschaffenheit oder die Betriebsbereitschaft von ''Gebäude''. Diese Attributart wird nur dann optional geführt, wenn der Zustand des Gebäudes vom nutzungsfähigen Zustand abweicht.','ax_gebaeude','DLKM','zustand');
