INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2100','Außer Betrieb, stillgelegt, verlassen','''Außer Betrieb, stillgelegt, verlassen'' bedeutet, dass das Gebäude auf Dauer nicht mehr bewohnt oder genutzt wird.','zustand','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2200','Verfallen, zerstört','''Verfallen, zerstört'' bedeutet, dass sich der ursprüngliche Zustand des Gebäudes durch menschliche oder zeitliche Einwirkungen so verändert hat, dass eine Nutzung nicht mehr möglich ist.','zustand','ax_gebaeude');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Dachgeschossausbau_Gebaeude','DGA','''Dachgeschossausbau'' ist ein Hinweis auf den Ausbau bzw. die Ausbaufähigkeit des Dachgeschosses.','ax_gebaeude','DLKM','dachgeschossausbau');
