INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4000','Offene Halle','Unter einer "Offenen Halle" ist eine Halle zu verstehen, bei der alle vier Seiten offen sind.Hallen, bei denen eine, zwei oder drei Seiten geschlossen sind, werden nur mit der entsprechenden Gebäudefunktion ohne Belegung der Bauart erfasst. Die offenen Gebäudeseiten sind mit "Besondere Gebäudelinie" Beschaffenheit "Offene Gebäudelinie" zu belegen','bauweise','ax_gebaeude');
