INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..*','Integer','BJA','''Baujahr'' ist das Jahr der Fertigstellung oder der baulichen Veränderung des Gebäudes.','ax_gebaeude','DLKM','baujahr');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,grunddatenbestand,kennung,definition,element,modellart,bezeichnung) VALUES ('1','AX_Gebaeudefunktion','DLKM','GFK','''Gebäudefunktion'' ist die zum Zeitpunkt der Erhebung vorherrschend funktionale Bedeutung des Gebäudes (Dominanzprinzip).','ax_gebaeude','DLKM','gebaeudefunktion');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3071','Polizei','''Polizei'' ist ein Gebäude für Polizeibedienstete, die in einem bestimmten Gebiet für Sicherheit und Ordnung zuständig sind.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3095','Gebäude zum S-Bahnhof','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3032','Theater, Oper','''Theater, Oper'' ist ein Gebäude, in dem Bühnenstücke aufgeführt werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2527','Reaktorgebäude','''Reaktorgebäude'' ist ein zentrales Gebäude eines Kernkraftwerkes, in dem aus radioaktivem Material mittels Kernspaltung Wärmeenergie erzeugt wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3037','Bibliothek, Bücherei','''Bibliothek, Bücherei'' ist ein Gebäude, in dem Bücher und Zeitschriften gesammelt, aufbewahrt und ausgeliehen werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3023','Hochschulgebäude (Fachhochschule, Universität)','''Hochschulgebäude (Fachhochschule, Universität)'' ist ein Gebäude, in dem Wissenschaften gelehrt und Forschung betrieben wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2621','Müllbunker','gebaeudefunktion','ax_gebaeude');
