INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2056','Apotheke','''Apotheke'' ist ein Geschäft, in dem Arzneimittel hergestellt und verkauft werden.','gebaeudefunktion','ax_gebaeude');
