INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2030','Kreditinstitut','''Kreditinstitut'' ist ein Gebäude, in dem Unternehmen gewerbsmäßig Geldgeschäfte (Verwaltung von Ersparnissen, Vergabe von Krediten) betreiben, die einen kaufmännisch eingerichteten Geschäftsbetrieb erfordern.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3017','Kreisverwaltung','gebaeudefunktion','ax_gebaeude');
