INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3281','Schutzhütte','''Schutzhütte'' ist ein Gebäude zum Schutz vor Unwetter.','gebaeudefunktion','ax_gebaeude');
