INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3290','Touristisches Informationszentrum','''Touristisches Informationszentrum'' ist eine Auskunftsstelle für Touristen.','gebaeudefunktion','ax_gebaeude');
