INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2052','Einkaufszentrum','''Einkaufszentrum'' ist ein Gebäude oder Gebäudekomplex, in dem mehrere Geschäfte untergebracht sind.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3075','Justizvollzugsanstalt','''Justizvollzugsanstalt'' ist ein Gebäude zur Unterbringung von Untersuchungshäftlingen und Strafgefangenen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2451','Spannwerk zur Drahtseilbahn','gebaeudefunktion','ax_gebaeude');
