INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3011','Parlament','''Parlament'' ist ein Gebäude, in dem die gesetzgebende Volksvertretung (Bundestag, Landtag) tagt.','gebaeudefunktion','ax_gebaeude');
