INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3050','Gebäude für Gesundheitswesen','''Gebäude für Gesundheitswesen'' ist ein Gebäude, das der ambulanten oder stationären Behandlung und Pflege von Patienten dient.','gebaeudefunktion','ax_gebaeude');
