INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1311','Ferienhaus','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1020','Wohnheim','''Wohnheim'' ist ein Gebäude, das nach seiner baulichen Anlage und Ausstattung zur Unterbringung von Studenten, Arbeitern u.a. bestimmt ist.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3035','Rundfunk, Fernsehen','''Rundfunk-, Fernsehen'' ist ein Gebäude, in dem Radio- und Fernsehprogramme produziert und gesendet werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3097','Gebäude zum Busbahnhof','''Gebäude zum Busbahnhof'' ist ein Gebäude auf dem Busbahnhof, das zur Abwicklung des Busverkehrs dient.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1313','Gartenhaus','''Gartenhaus'' ist ein eingeschossiges Gebäude in einfacher Ausführung, z.B.ohne Feuerstätte und ohne Toilette. Es dient hauptsächlich dem Unterbringen von Gartengeräten oder dem Überwintern von Pflanzen. Stellt es bei der Nutzung des Gartens eine Ruhe oder Erholungsstätte dar, ist es nur zum vorübergehenden Aufenthalt gedacht, nicht jedoch zum Wohnen oder Übernachten.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3041','Kirche','''Kirche'' ist ein Gebäude, in dem sich Christen zu Gottesdiensten versammeln.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2726','Scheune und Stall','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2310','Gebäude für Handel und Dienstleistung mit Wohnen','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1221','Bauernhaus','''Bauernhaus'' ist das Wohn- und Betriebsgebäude eines Landwirts.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1223','Forsthaus','''Forsthaus'' ist ein Gebäude, das gleichzeitig Wohnhaus und Dienststelle der Försterin oder des Försters ist.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2010','Gebäude für Handel und Dienstleistungen','''Gebäude für Handel und Dienstleistungen'' ist ein Gebäude, in dem Arbeitsleistungen, die nicht der Produktion von materiellen Gütern dienen, angeboten werden. Dazu gehört u.a. der Handel (Ankauf, Transport, Verkauf) mit Gütern, Kapital oder Wissen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2612','Toilette','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2461','Parkhaus','''Parkhaus'' ist ein Gebäude, in dem Fahrzeuge auf mehreren Etagen abgestellt werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1023','Schwesternwohnheim','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2150','Speditionsgebäude','''Speditionsgebäude'' bezeichnet ein Gebäude mit technischen, organisatorischen und wirtschaftlichen Einrichtungen, die der Beförderung von Gütern über räumliche Entfernungen dienen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2623','Gebäude der Abfalldeponie','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('9998','Nach Quellenlage nicht zu spezifizieren','''Nach Quellenlage nicht zu spezifizieren'' bedeutet, dass keine Aussage über die Werteart gemacht werden kann.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3080','Friedhofsgebäude','''Friedhofsgebäude'' ist ein Gebäude, das zur Aufrechterhaltung des Friedhofbetriebes dient (z.B. Verwaltung, Leichenhalle, Krematorium).','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3072','Feuerwehr','''Feuerwehr'' ist ein Gebäude der Feuerwehr, in dem Personen und Geräte zur Brandbekämpfung sowie zu anderen Hilfeleistungen untergebracht sind.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2072','Jugendherberge','''Jugendherberge'' ist eine zur Förderung von Jugendreisen dienende Aufenthalts- und Übernachtungsstätte.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2463','Garage','''Garage'' ist ein Gebäude, in dem Fahrzeuge abgestellt werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3020','Gebäude für Bildung und Forschung','''Gebäude für Bildung und Forschung'' ist ein Gebäude, in dem durch Ausbildung Wissen und Können auf verschiedenen Gebieten vermittelt werden bzw. wo neues Wissen durch wissenschaftliche Tätigkeit gewonnen wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2131','Waschstraße, Waschanlage, Waschhalle','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2523','Umformer','''Umformer'' ist ein kleines Gebäude in dem ein Transformator zum Umformen von Gleichstrom in Wechselstrom oder von Gleichstrom in Gleichstrom anderer Spannung untergebracht ist.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2441','Werft (Halle)','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2320','Gebäude für Gewerbe und Industrie mit Wohnen','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2055','Kiosk','''Kiosk'' ist ein kleines in meist leichter Bauweise errichtetes Gebäude, das als Verkaufseinrichtung für ein beschränktes Warenangebot dient.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2501','Gebäude zur Energieversorgung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3200','Gebäude für Erholungszwecke','''Gebäude für Erholungszwecke'' ist ein Gebäude zur Freizeitgestaltung mit dem Ziel der Erhaltung und Wiederherstellung der Leistungsfähigkeit des Menschen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3019','Finanzamt','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2728','Reithalle','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3061','Jugendfreizeitheim','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2094','Spielkasino','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2000','Gebäude für Wirtschaft oder Gewerbe','''Gebäude für Wirtschaft oder Gewerbe'' ist ein Gebäude, das der Produktion von Waren, der Verteilung von Gütern und dem Angebot von Dienstleistungen dient.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2113','Brauerei','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3098','Empfangsgebäude Schifffahrt','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3064','Obdachlosenheim','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2410','Betriebsgebäude für Straßenverkehr','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3272','Gewächshaus (Botanik)','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2180','Gebäude für betriebliche Sozialeinrichtung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3091','Bahnhofsgebäude','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3090','Empfangsgebäude','''Empfangsgebäude'' ist ein Gebäude mit Wartesaal, Fahrkarten- und Gepäckschalter zur Abwicklung des Straßen-, Schienen-, Seilbahn-, Luft- und Schiffsverkehrs.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2111','Fabrik','''Fabrik'' ist ein Gebäude mit technischen Anlagen zur Herstellung von Waren in großen Mengen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1220','Land- und forstwirtschaftliches Wohn- und Betriebsgebäude','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2080','Gebäude für Bewirtung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2051','Kaufhaus','''Kaufhaus'' ist ein Gebäude, meist mit mehreren Stockwerken, in dem breite Warensortimente zum Kauf angeboten werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2090','Freizeit- und Vergnügungsstätte','''Freizeit- und Vergnügungsstätte'' ist ein Gebäude, in dem man in seiner Freizeit bestimmte Angebote wahrnehmen kann.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1312','Wochenendhaus','''Wochenendhaus'' ist ein Gebäude, in dem dauerhaftes Wohnen möglich, aber nicht gestattet ist. Es dient nur zum zeitlich begrenzten Aufenthalt in der Freizeit, beispielsweise am Wochenende oder im Urlaub und steht i.d.R. in einem besonders dafür ausgewiesenen Gebiet (Wochenendhausgebiet).','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2060','Messehalle','''Messehalle'' ist ein Gebäude, das zur Ausstellung von Kunstgegenständen oder Wirtschaftsgütern dient.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3070','Gebäude für Sicherheit und Ordnung','''Gebäude für Sicherheit und Ordnung'' ist ein Gebäude, das für Personen und Gegenstände dient, die zur Verhütung oder Bekämpfung von Rechtsverletzungen und zum Katastrophenschutz eingesetzt werden, oder zur Unterbringung von Strafgefangenen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2110','Produktionsgebäude','''Produktionsgebäude'' ist ein Gebäude, das zur Herstellung von Wirtschaftsgütern dient.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2200','Sonstiges Gebäude für Gewerbe und Industrie','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3046','Moschee','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3280','Gebäude für andere Erholungseinrichtung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3031','Schloss','''Schloss'' ist ein Gebäude, das als repräsentativer Wohnsitz vor allem des Adels dient oder diente.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2083','Kantine','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3024','Forschungsinstitut','''Forschungsinstitut'' ist ein Gebäude, in dem Forschung betrieben wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2073','Hütte (mit Übernachtungsmöglichkeit)','''Hütte (mit Übernachtungsmöglichkeit)'' ist ein Gebäude außerhalb von Ortschaften, meist in den Bergen, in dem Menschen übernachten und Schutz suchen können.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2141','Kühlhaus','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3043','Kapelle','''Kapelle'' ist ein kleines Gebäude (Gebets-, Tauf-, Grabkapelle) für (christliche) gottesdienstliche Zwecke.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3045','Gotteshaus','''Gotteshaus'' ist ein Gebäude, in dem Gläubige einer nichtchristlichen Religionsgemeinschaft religiöse Handlungen vollziehen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2522','Umspannwerk','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3073','Kaserne','''Kaserne'' ist ein Gebäude zur ortsfesten Unterbringung von Angehörigen der Bundeswehr und der Polizei sowie deren Ausrüstung.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2093','Kegel-, Bowlinghalle','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2591','Pumpwerk (nicht für Wasserversorgung)','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3036','Veranstaltungsgebäude','''Veranstaltungsgebäude'' ist ein Gebäude, das hauptsächlich für kulturelle Zwecke wie z.B. Aufführungen, Ausstellungen, Konzerte genutzt wird','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2220','Wetterstation','''Wetterstation'' ist ein Gebäude, in dem meteorologische Daten erfasst und ausgewertet werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2143','Lagerhalle, Lagerschuppen, Lagerhaus','''Lagerhalle, Lagerschuppen, Lagerhaus'' ist ein Gebäude zur Vorratshaltung von Gütern (z. B. Material, Fertigerzeugnissen).','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2430','Betriebsgebäude für Flugverkehr','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3211','Sport-, Turnhalle','''Sport-, Turnhalle'' ist ein Gebäude, das für den Turnunterricht und für sportliche Betätigungen in der Freizeit errichtet und dementsprechend ausgestattet ist.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3013','Post','''Post ist ein Gebäude, in dem die Post Dienstleistungen anbietet.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3100','Gebäude für öffentliche Zwecke mit Wohnen','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2610','Gebäude zur Abwasserbeseitigung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3270','Gebäude im botanischen Garten','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2160','Gebäude für Forschungszwecke','''Gebäude für Forschungszwecke'' ist ein Gebäude, in dem Forschung betrieben wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3048','Kloster','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1222','Wohn- und Wirtschaftsgebäude','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3021','Allgemein bildende Schule','''Allgemein bildende Schule'' ist ein Gebäude, in dem Kindern, Jugendlichen und Erwachsenen durch planmäßigen Unterricht Wissen vermittelt wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3015','Gericht','''Gericht'' ist ein Gebäude, in dem Rechtsprechung und Rechtspflege stattfinden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2590','Gebäude zur Versorgungsanlage','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3212','Gebäude zum Sportplatz','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3034','Museum','''Museum'' ist ein Gebäude, in dem Sammlungen von (historischen) Objekten oder Reproduktionen davon ausgestellt werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3016','Botschaft, Konsulat','''Botschaft, Konsulat'' ist ein Gebäude, in dem eine ständige diplomatische Vertretung ersten Rangs eines fremden Staates oder einer internationalen Organisation untergebracht ist.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2091','Festsaal','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('3220','Badegebäude','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2142','Speichergebäude','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1110','Wohngebäude mit Gemeinbedarf','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Gemischt genutztes Gebäude mit Wohnen','''Gemischt genutztes Gebäude mit Wohnen'' ist ein Gebäude, in dem sowohl gewohnt wird, als auch Teile des Gebäude zum Anbieten von Dienstleistungen, zur Durchführung von öffentlichen oder privaten Verwaltungsarbeiten, zur gewerblichen oder industriellen Tätigkeit genutzt werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2411','Straßenmeisterei','''Straßenmeisterei'' ist das Verwaltungsgebäude einer Dienststelle, die für den ordnungsgemäßen Zustand von Straßen verantwortlich ist.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2521','Elektrizitätswerk','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2400','Betriebsgebäude zu Verkehrsanlagen (allgemein)','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3000','Gebäude für öffentliche Zwecke','''Gebäude für öffentliche Zwecke'' ist ein Gebäude das der Allgemeinheit dient.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2500','Gebäude zur Versorgung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2600','Gebäude zur Entsorgung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2528','Turbinenhaus','''Turbinenhaus'' ist ein Gebäude, in dem eine Kraftmaschine die Energie von strömendem Dampf, Gas, Wasser oder Wind unmittelbar in elektrische Energie umsetzt.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2071','Hotel, Motel, Pension','''Hotel, Motel, Pension'' ist ein Gebäude mit Beherbergungs- und/oder Verpflegungsbetrieb nach Service, Ausstattung und Qualität in verschiedene Kategorien eingeteilt. Das Motel ist besonders eingerichtet für Reisende mit Kraftfahrzeug an verkehrsreichen Straßen.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2212','Wassermühle','''Wassermühle'' ist ein Gebäude mit einem Mühlrad, das von Wasser angetrieben wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2611','Gebäude der Kläranlage','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2170','Gebäude für Grundstoffgewinnung','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2444','Bootshaus','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2460','Gebäude zum Parken','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3014','Zollamt','''Zollamt'' ist ein Gebäude für die Zollabfertigung an der Staatsgrenze (Grenzzollamt) oder im Inland (Binnenzollamt).','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Wohngebäude','''Wohngebäude'' ist ein Gebäude, das zum Wohnen genutzt wird.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2442','Dock (Halle)','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2171','Bergwerk','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2512','Pumpstation','''Pumpstation'' ist ein Gebäude an einem Rohrleitungssystem, in dem eine oder mehrere Pumpen eingebaut sind.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3033','Konzertgebäude','''Konzertgebäude'' ist ein Gebäude, in dem Musikaufführungen stattfinden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2464','Fahrzeughalle','''Fahrzeughalle'' ist ein Gebäude, in dem Fahrzeuge abgestellt, inspiziert und repariert werden.','gebaeudefunktion','ax_gebaeude');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3060','Gebäude für soziale Zwecke','''Gebäude für soziale Zwecke'' ist ein Gebäude, in dem ältere Menschen, Obdachlose, Jugendliche oder Kinder betreut werden.','gebaeudefunktion','ax_gebaeude');
