INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1106','Radweg','''Radweg'' ist ein Weg, der als besonders gekennzeichneter und abgegrenzter Teil einer Straße oder mit selbständiger Linienführung für den Fahrradverkehr bestimmt ist','art','ax_wegpfadsteig');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1111','Skaterstrecke','''Skaterstrecke'' ist ein für Skater besonders ausgebauter asphaltierter Weg.','art','ax_wegpfadsteig');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1105','Karren- und Ziehweg','Karrenweg ist ein Weg im Gebirge, der meist sehr steil ist und nur mit einem Gespann befahren werden kann. Ziehweg ist ein Weg, der der Holzabfuhr im Gebirge dient.','art','ax_wegpfadsteig');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1103','Fußweg','''Fußweg'' ist ein Weg, der auf Grund seines Ausbauzustandes nur von Fußgängern zu begehen ist.','art','ax_wegpfadsteig');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1110','Rad- und Fußweg','''Rad- und Fußweg'' ist ein Weg, der als besonders gekennzeichneter und abgegrenzter Teil einer Straße oder mit selbständiger Linienführung ausschließlich für den Fahrrad- und Fußgängerverkehr bestimmt ist.','art','ax_wegpfadsteig');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1108','Wattenweg','art','ax_wegpfadsteig');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NAM','''Name'' ist der Eigenname von ''Weg, Pfad, Steig''.','ax_wegpfadsteig','DLKM','name');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BEZ','''Bezeichnung'' ist die von einer Fachstelle vergebene Kennziffer von ''Weg, Pfad, Steig''.','ax_wegpfadsteig','DLKM','bezeichnung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Markierung_WegPfadSteig','MKG','''Markierung'' ist die Kennzeichnung einer Route im Wegenetz.','ax_wegpfadsteig','DLKM','markierung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1402','Gekennzeichneter Radwanderweg','''Gekennzeichneter Radwanderweg'' ist ein als Radwanderweg eindeutig markierter Weg.','markierung','ax_wegpfadsteig');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1401','Gekennzeichneter Wanderweg','''Gekennzeichneter Wanderweg'' ist ein als Wanderweg eindeutig markierter Weg.','markierung','ax_wegpfadsteig');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1403','Gekennzeichnete Skaterstrecke','''Gekennzeichnete Skaterstrecke'' ist ein als Skaterstrecke eindeutig markierter Weg.','markierung','ax_wegpfadsteig');
INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_platz','DLKM','42009','REO','[E] ''Platz'' ist eine Verkehrsfläche in Ortschaften oder eine ebene, befestigte oder unbefestigte Fläche, die bestimmten Zwecken dient (z. B. für Verkehr, Märkte, Festveranstaltungen).','{"ax_tatsaechlichenutzung"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Funktion_Platz','FKT','''Funktion'' ist die zum Zeitpunkt der Erhebung objektiv erkennbare oder feststellbare vorkommende Nutzung.','ax_platz','DLKM','funktion');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5320','Rastplatz','''Rastplatz'' ist eine Anlage zum Halten, Parken oder Rasten der Verkehrsteilnehmer mit unmittelbarem Anschluss zur Straße ohne Versorgungseinrichtung, ggf. mit Toiletten.','funktion','ax_platz');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('5340','Marktplatz','funktion','ax_platz');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5330','Raststätte','''Raststätte'' ist eine Anlage an Verkehrsstraßen mit Bauwerken und Einrichtungen zur Versorgung und Erholung von Reisenden.','funktion','ax_platz');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5310','Parkplatz','''Parkplatz'' ist eine zum vorübergehenden Abstellen von Fahrzeugen bestimmte Fläche.','funktion','ax_platz');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5350','Festplatz','''Festplatz'' ist eine Fläche, auf der zeitlich begrenzte Festveranstaltungen stattfinden.','funktion','ax_platz');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5130','Fußgängerzone','''Fußgängerzone'' ist ein dem Fußgängerverkehr vorbehaltener Bereich, in dem ausnahmsweise öffentlicher Personenverkehr, Lieferverkehr oder Fahrradverkehr zulässig sein kann.','funktion','ax_platz');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Lagebezeichnung','NAM','''Name'' ist der Eigenname von ''Platz''.','ax_platz','DLKM','name');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','ZNM','''Zweitname'' ist der touristische oder volkstümliche Name von ''Platz''.','ax_platz','DLKM','zweitname');
INSERT INTO alkis_elemente(name,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_turm','51001','REO','[E] ''Turm'' ist ein hoch aufragendes, auf einer verhältnismäßig kleinen Fläche freistehendes Bauwerk.','{"ax_bauwerkeeinrichtungenundsonstigeangaben","ag_objekt"}','DLKM','Objektart');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..*','''Turm'' zeigt auf eine ''Lagebezeichnung mit Hausnummer''.','ax_lagebezeichnungmithausnummer','51001-12002','weistZum','ax_turm','DLKM','zeigtAuf');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','Length','HHO','''Objekthöhe'' ist die Höhendifferenz in [m] zwischen dem höchsten Punkt des eigentlichen Bauwerks ''Turm'' und der Geländeoberfläche. Aufgesetzte Anlagen (z.B. Antenne) zählen nicht zur Objekthöhe. Bei mehrfacher Bauwerksfunktion wird immer bis zurm höchsten Punkt des Turms gemessen.','ax_turm','DLKM','objekthoehe');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NAM','''Name'' ist der Eigenname von ''Turm''.','ax_turm','DLKM','name');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Zustand_Turm','ZUS','''Zustand'' ist der Zustand von ''Turm''.','ax_turm','DLKM','zustand');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2100','Außer Betrieb, stillgelegt, verlassen','''Außer Betrieb, stillgelegt; verlassen'' bedeutet, dass sich der Turm nicht mehr in regelmäßiger, der Bestimmung entsprechenden Nutzung befindet.','zustand','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2200','Verfallen, zerstört','''Verfallen, zerstört'' bedeutet, dass sich der ursprüngliche Zustand des Turmes durch menschliche oder zeitliche Einwirkungen so verändert hat, dass eine Nutzung nicht mehr möglich ist.','zustand','ax_turm');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1..2','AX_Bauwerksfunktion_Turm','BWF','''Bauwerksfunktion'' beschreibt die Art oder Funktion von ''Turm''.','ax_turm','DLKM','bauwerksfunktion');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1004','Kontrollturm','''Kontrollturm'' (Tower) ist ein Bauwerk auf dem Fluggelände, in dem die für die Lenkung und Überwachung des Flugverkehrs erforderlichen Anlagen und Einrichtungen untergebracht sind.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1002','Kirchturm, Glockenturm','''Kirchturm, Glockenturm'' ist ein freistehender Turm, der die Glockenstube mit den Glocken aufnimmt.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1011','Bohrturm','''Bohrturm'' ist ein zur Gewinnung von Erdöl und Erdgas verwendetes, meist aus einer Stahlkonstruktion bestehendes Gerüst, in dem das Bohrgestänge aufgehängt ist.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1003','Aussichtsturm','''Aussichtsturm'' ist ein Bauwerk, das ausschließlich der Fernsicht dient.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('9999','Sonstiges','''Sonstiges'' bedeutet, dass die Funktion bekannt, aber nicht in der Attributwertliste aufgeführt ist.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1005','Kühlturm','''Kühlturm'' ist eine turmartige Kühlanlage (Nass- oder Trockenkühlturm), in der erwärmtes Kühlwasser insbesondere von Kraftwerken rückgekühlt wird.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1012','Schloss-, Burgturm','''Schloss-, Burgturm'' ist ein Turm innerhalb einer Schloss- bzw. einer Burganlage, auch Bergfried genannt.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1008','Sende-, Funkturm, Fernmeldeturm','''Sende-, Funkturm, Fernmeldeturm'' ist ein Bauwerk, ausgerüstet mit Sende - und Empfangsantennen zum Übertragen und Empfangen von Nachrichten aller Arten von Telekommunikation.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1007','Feuerwachturm','''Feuerwachturm'' ist ein Turm, der zum Erkennen von Gefahren (Feuer) dient.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1010','Förderturm','''Förderturm'' ist ein Turm über einem Schacht. An Förderseile, die über Seilscheiben im Turm geführt werden, werden Lasten in den Schacht gesenkt oder aus dem Schacht gehoben.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1006','Leuchtturm','''Leuchtturm'' ist ein als Schifffahrtszeichen dienender hoher Turm, ausgerüstet mit einem starken Leuchtfeuer verschiedener Kennungen an der Turmspitze und mit anderen, der Schifffahrt dienenden Signalen.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('9998','Nach Quellenlage nicht zu spezifizieren','''Nach Quellenlage nicht zu spezifizieren'' bedeutet, dass zum Zeitpunkt der Erhebung keine Funktion zuweisbar war.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1001','Wasserturm','''Wasserturm'' ist ein hochgelegenes Bauwerk mit einem Behälter, in dem Wasser für die Wasserversorgung und Konstanthaltung des Wasserdruckes gespeichert wird.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1009','Stadt-, Torturm','''Stadtturm'' ist ein historischer Turm, der das Stadtbild prägt. ''Torturm'' ist der auf einem Tor stehende Turm, wobei das Tor allein stehen oder in eine Befestigungsanlage eingebunden sein kann.','bauwerksfunktion','ax_turm');
INSERT INTO alkis_elemente(name,kennung,grunddatenbestand,definition,abgeleitet_aus,modellart,type) VALUES ('ax_lagebezeichnung','12004','DLKM','Der Auswahldatentyp ''Lagebezeichnung'' beinhaltet eine verschlüsselte oder unverschlüsselte Lagebezeichnung.','{}','DLKM','Auswahldatentyp');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,grunddatenbestand,kennung,definition,element,modellart,bezeichnung) VALUES ('1','CharacterString','DLKM','UNV','''Unverschlüsselte Lagebezeichnung'' ist die unverschlüsselte Bezeichnung einer Lage.','ax_lagebezeichnung','DLKM','unverschluesselt');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,grunddatenbestand,kennung,definition,element,modellart,bezeichnung) VALUES ('1','AX_VerschluesselteLagebezeichnung','DLKM','SCH','''Verschlüsselte Lagebezeichnung'' ist ein eindeutiges Fachkennzeichen (siehe Katalog der verschlüsselten Lagebezeichnungen). Die Attributart setzt sich zusammen aus dem Gemeindekennzeichen mit den Verschlüsselungen für 1. Spalte: Land 2. Spalte: Regierungsbezirk 3. Spalte: Kreis (kreisfreie Stadt) 4. Spalte: Gemeinde 5. Spalte: Lage','ax_lagebezeichnung','DLKM','verschluesselt');
INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type,bildungsregeln) VALUES ('ax_klassifizierungnachstrassenrecht','DLKM','71001','REO','[E] ''Klassifizierung nach Straßenrecht'' ist die auf den Grund und Boden bezogene Beschränkung, Belastung oder andere Eigenschaft einer Fläche nach öffentlichen, straßenrechtlichen Vorschriften.','{"ag_objekt"}','DLKM','Objektart','Die Attributart ''Art der Festlegung'' ist objektbildend.');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Dienststelle_Schluessel','AFS','''Ausführende Stelle'' ist die Stelle, die für die Festlegung zuständig ist (siehe Katalog der Dienststellen).','ax_klassifizierungnachstrassenrecht','DLKM','ausfuehrendestelle');
