INSERT INTO alkis_elemente(konsistenzbedingungen,name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type,erfassungskriterien,bildungsregeln) VALUES ('Lückenlose und überschneidungsfreie Flächendeckung der Objekte der Objektart Flurstück. Die Positionen der Knoten der Kante müssen zugleich identisch sein mit den Positionen der Endpunkte der Linie. Jede Linie ist durch genau zwei Positionen bestimmt. Es muß entweder die Relation ''zeigt_auf'' oder ''weist_auf'' belegt sein. Jedes Flurstück gehört zu genau einer Gemarkung oder einer Flur/Gemarkungsteil.','ax_flurstueck','DLKM','11001','REO','[A] ''Flurstück'' ist ein Teil der Erdoberfläche, der von einer im Liegenschaftskataster festgelegten Grenzlinie umschlossen und mit einer Nummer bezeichnet ist. Es ist die Buchungseinheit des Liegenschaftskatasters.','{"ta_multisurfacecomponent","ax_flurstueck_kerndaten"}','DLKM','Objektart','Räumlich getrennt liegende Flurstücksteile sollen zerlegt und als eigene Flurstücke geführt werden. Im Rahmen der Migration sind räumlich getrennt liegende Flurstücke jedoch zugelassen. In diesem Fall besteht das Flurstück aus mindestens zwei Maschen.','Die Attributart ''Flurstückskennzeichen'' ist objektbildend.');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..*','''Flurstück'' gehört anteilig zu ''Flurstück''. Die Relationsart kommt nur vor bei Flurstücken, die eine Relation zu einer Buchungsstelle mit einer der Buchungsarten Anliegerweg, Anliegergraben oder Anliegerwasserlauf aufweisen.','ax_flurstueck','11001.1-11001.2','beziehtSichAufFlurstueck','ax_flurstueck','DLKM','gehoertAnteiligZu');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..*','Es handelt sich um die inverse Relationsrichtung.','ax_flurstueck','(INV)11001.1-11001.2','gehoertAnteiligZu','ax_flurstueck','DLKM','beziehtSichAufFlurstueck');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,grunddatenbestand,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..*','''Flurstück'' weist auf ''Lagebezeichnung mit Hausnummer''.','ax_lagebezeichnungmithausnummer','DLKM','11001-12002','gehoertZu','ax_flurstueck','DLKM','weistAuf');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,grunddatenbestand,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..*','''Flurstück'' zeigt auf ''Lagebezeichnung ohne Hausnummer''.','ax_lagebezeichnungohnehausnummer','DLKM','11001-12001','gehoertZu','ax_flurstueck','DLKM','zeigtAuf');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,grunddatenbestand,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('1','Ein (oder mehrere) Flurstück(e) ist (sind) unter genau einer Buchungsstelle gebucht. Bei Anteilsbuchungen ist dies nur dann möglich, wenn ein fiktives Buchungsblatt angelegt wird. Wird ein fiktives Buchunsblatt verwendet, ist die Kardinalität dieser Attributart 1..1.','ax_buchungsstelle','DLKM','11001-21008','grundstueckBestehtAus','ax_flurstueck','DLKM','istGebucht');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,grunddatenbestand,kennung,definition,element,modellart,bezeichnung) VALUES ('0..*','AX_Dienststelle_Schluessel','DLKM','ZST','''Flurstück'' wird verwaltet von ''Dienststelle''. Diese Attributart wird nur dann belegt, wenn eine fachliche Zuständigkeit über eine Gemarkung bzw. Gemarkungsteil/Flur nicht abgebildet werden kann. Die Attributart enthält den Dienststellenschlüssel der Stelle, die fachlich für ein Flurstück zustandig ist.','ax_flurstueck','DLKM','zustaendigestelle');
INSERT INTO alkis_elemente(name,kennung,abgeleitet_aus,modellart,type) VALUES ('ax_reservierungsauftrag_gebietskennung','16004','{}','DLKM','Auswahldatentyp');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,element,modellart,bezeichnung) VALUES ('1','AX_Gemarkung_Schluessel','GMK','ax_reservierungsauftrag_gebietskennung','DLKM','gemarkung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,element,modellart,bezeichnung) VALUES ('1','AX_Buchungsblattbezirk_Schluessel','BBZ','ax_reservierungsauftrag_gebietskennung','DLKM','buchungsblattbezirk');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,element,modellart,bezeichnung) VALUES ('1','AX_GemarkungsteilFlur_Schluessel','FLR','ax_reservierungsauftrag_gebietskennung','DLKM','flur');
INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type,bildungsregeln) VALUES ('ax_aufnahmepunkt','DLKM','13001','ZUSO','[E] ''Aufnahmepunkt'' ist ein Punkt des Lagefestpunktfeldes - Aufnahmepunktfeld und dient der örtlichen Aufnahme von Objektpunkten. Das Aufnahmepunktfeld ist eine Verdichtungsstufe des Lagefestpunktfeldes - Trigonometrisches Festpunktfeld (Grundlagenvermessung).','{"ax_netzpunkt"}','DLKM','Objektart','Das ZUSO besteht das aus einem oder mehreren REO ''Punktort''.');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..*','''Aufnahmepunkt'' hat ''Sicherungspunkt''.','ax_sicherungspunkt','13001-13002','gehoertZu','ax_aufnahmepunkt','DLKM','hat');
INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type,bildungsregeln) VALUES ('ax_buchungsstelle','DLKM','21008','NREO','[E] ''Buchungsstelle'' ist die unter einer laufenden Nummer im Verzeichnis des Buchungsblattes eingetragene Buchung.','{"aa_nreo"}','DLKM','Objektart','Die Attributarten ''Buchungsart'' und ''Laufende Nummer'' sind objektbildend. Die Buchungsarten mit Wertearten 1101, 1102, 1401 bis 1403, 2201 bis 2205 und 2401 bis 2404 können nur auf einem Fiktiven Blatt vorkommen. Die Attributart ''Anteil'' ist dann immer zu belegen.');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,element,modellart,bezeichnung) VALUES ('0..*','Die Relation ''Buchungsstelle'' hat Vorgänger ''Buchungsstelle'' gibt Auskunft darüber, aus welchen Buchungsstellen die aktuelle Buchungsstelle entstanden ist.','ax_buchungsstelle','21008.9-21008.10','ax_buchungsstelle','DLKM','hatVorgaenger');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..1','Die ''Buchungsstelle'' wird verwaltet von ''Verwaltung''.','ax_verwaltung','21008-21004','beziehtSichAuf','ax_buchungsstelle','DLKM','wirdVerwaltetVon');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,element,modellart,bezeichnung) VALUES ('0..*','''Buchungsstelle'' bezieht sich auf ''Buchungsblatt''.','ax_buchungsblatt','21008-21007.1','ax_buchungsstelle','DLKM','beziehtSichAuf');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,grunddatenbestand,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('1','''Buchungsstelle'' ist Teil von ''Buchungsblatt''. Bei ''Buchungsart'' mit einer der Wertearten für aufgeteilte Buchungen (Wertearten 1101, 1102, 1401 bis 1403, 2201 bis 2205 und 2401 bis 2404) muss die Relation zu einem ''Buchungsblatt'' und der ''Blattart'' mit der Werteart ''Fiktives Blatt'' bestehen.','ax_buchungsblatt','DLKM','21008-21007.2','bestehtAus','ax_buchungsstelle','DLKM','istBestandteilVon');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,element,modellart,bezeichnung) VALUES ('0..*','Eine ''Buchungsstelle'' verweist mit ''durch'' auf eine andere ''Buchungsstelle'' auf einem anderen Buchungsblatt (herrschend). Die Buchungsstelle ist belastet durch ein Recht, dass ''durch'' die andere Buchungsstelle an ihr ausgeübt wird.','ax_buchungsstelle','21008.7-21008.8','ax_buchungsstelle','DLKM','durch');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,grunddatenbestand,kennung,element,modellart,bezeichnung) VALUES ('0..*','''Buchungsstelle'' verweist auf ''Flurstück''.','ax_flurstueck','DLKM','21008-11001','ax_buchungsstelle','DLKM','verweistAuf');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,grunddatenbestand,kennung,inv__relation,element,modellart,bezeichnung) VALUES ('0..*','Diese Relationsart legt fest, welche Flurstücke ein Grundstück bilden. Nur bei der ''Buchungsart'' mit den Wertearten 1100, 1101 und 1102 muss die Relationsart vorhanden sein, sofern nicht ein Objekt AX_HistorischesFlurstueckOhneRaumbezug über die Relationsart ''istGebucht'' auf die Buchungsstelle verweist. Es handelt sich um die inverse Relationsrichtung.','ax_flurstueck','DLKM','(INV)11001-21008','istGebucht','ax_buchungsstelle','DLKM','grundstueckBestehtAus');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,grunddatenbestand,kennung,element,modellart,bezeichnung) VALUES ('0..*','Eine ''Buchungsstelle'' verweist mit ''zu'' auf eine andere ''Buchungsstelle'' des gleichen Buchungsblattes (herrschend).','ax_buchungsstelle','DLKM','21008.1-21008.2','ax_buchungsstelle','DLKM','zu');
INSERT INTO alkis_relationsart(kardinalitaet,anmerkung,zielobjektart,kennung,element,modellart,bezeichnung) VALUES ('0..*','Eine ''Buchungsstelle'' verweist mit ''an'' auf eine andere ''Buchungsstelle'' auf einem anderen Buchungsblatt. Die Buchungsstelle kann ein Recht (z.B. Erbbaurecht) oder einen Miteigentumsanteil ''an'' der anderen Buchungsstelle haben Die Relation zeigt stets vom begünstigten Recht zur belasteten Buchung (z.B. Erbbaurecht hat ein Recht ''an'' einem Grundstück).','ax_buchungsstelle','21008.5-21008.6','ax_buchungsstelle','DLKM','an');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','Date','ZDE','''Zeitpunkt der Eintragung'' beinhaltet das Datum, an dem die Rechtsänderung stattgefunden hat (z.B. Eintragung im Grundbuch).','ax_buchungsstelle','DLKM','zeitpunktdereintragung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BTX','''Buchungstext'' enthält zusätzliche Angaben zur Buchungsart (z.B. die genaue Bezeichnung von Nutzungsrechten).','ax_buchungsstelle','DLKM','buchungstext');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,grunddatenbestand,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Anteil','DLKM','ANT','''Anteil'' ist die Angabe des Miteigentumsanteils am Grundstück oder des Anteils am Recht. Das Attribut setzt sich zusammen aus: 1. Spalte: Zähler 2. Spalte: Nenner','ax_buchungsstelle','DLKM','anteil');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NRA','''Nummer im Aufteilungsplan'' ist die Nummer entsprechend der Teilungserklärung über die Aufteilung des Gebäudes in Lage und Größe der im Sondereigentum und der im gemeinschaftlichen Eigentum stehenden Gebäudeteile.','ax_buchungsstelle','DLKM','nummerimaufteilungsplan');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BUB','''Beschreibung des Umfangs der Buchung'' ist eine nähere Beschreibung der Buchungsart (z.B. ''von der Quelle bis zur Brücke'').','ax_buchungsstelle','DLKM','beschreibungdesumfangsderbuchung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BSO','''Beschreibung des Sondereigentums'' ist die Beschreibung von Wohnungseigentum an Wohnungen und von Teileigentum an nicht zu Wohnzwecken dienenden Räumen.','ax_buchungsstelle','DLKM','beschreibungdessondereigentums');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,grunddatenbestand,kennung,definition,element,modellart,bezeichnung) VALUES ('1','CharacterString','DLKM','LNR','''Laufende Nummer'' ist die eindeutige Nummer der Buchungsstelle auf dem Buchungsblatt.','ax_buchungsstelle','DLKM','laufendenummer');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,grunddatenbestand,kennung,definition,element,modellart,bezeichnung) VALUES ('1','AX_Buchungsart_Buchungsstelle','DLKM','BAR','''Buchungsart'' bezeichnet die Art der Buchung.','ax_buchungsstelle','DLKM','buchungsart');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('5201','Anliegerweg','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2505','Anteil am Anteil zum Gebäudeeigentum','Hier wird der Anteil an dem Anteil zum Gebäudeeigentum im Grundbuch eingetragen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1503','Anteil am Anteil zum ungetrennten Hofraum','Hier wird der Anteil an dem Anteil an dem ungetrennten Hofraum im Grundbuch eingetragen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1101','Aufgeteiltes Grundstück WEG','Ein aufgeteiltes GrundstückWEG ist die Zusammenfassung aller in Wohnungs- oder Teileigentum aufgeteilten Anteile eines Grundstücks. Es handelt sich daher um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('5203','Anliegerwasserlauf, Anliegergewässer','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5101','Von Buchungspflicht befreit Par. 3 Abs. 2 GBO','Grundstücke nach Par. 3 Abs. 2 sind von der Buchungspflicht befreit und werden auf dem Katasterblatt gebucht.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2110','Hauberge','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1502','Anteil an Miteigentumsanteil Par. 3 Abs. 4 GBO','Hier wird der Anteil an dem Miteigentumsanteil nach Par. 3 Abs. 4 GBO im Grundbuch eingetragen','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3100','Vermerk subjektiv dinglicher Rechte (Par. 9 GBO)','Der Vermerk ist ein Hinweis auf eine in Abteilung II des Grundbuchs des dienenden Grundstücks eingetragene Belastung. Er selbst ist kein Recht; seine Eintragung sichert lediglich, dass bei einer Aufhebung des Rechts im Grundbuch des dienenden Grundstücks die Bewilligung derer erforderlich ist, die der Rechtsänderung nach Par. 876 S. 2, 877, 888 BGB zustimmen müssen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2109','Stavenrecht','Ist ein mit dem Erbbaurecht vergleichbares Recht in den nordfriesischen Küstenregionen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2305','Anteil am Gebäudeeigentum','Ist der Anteil des Nutzungsberechtigten für die Nutzung des auf dem Grundstück stehenden Gebäudes.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2101','Erbbaurecht','Veräußerliches und vererbliches grundstücksgleiches Recht, auf oder unter der Erdoberfläche eines (in der Regel) fremden Grundstücks ein Bauwerk zu haben.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2105','Bergwerksrecht','Bergwerksrecht ist das ausschließliche Recht, in einem bestimmten Feld die in der Bewilligung bezeichneten Bodenschätze aufzusuchen und zu gewinnen (Par.9 I, Par. 8 BBergG vom 13.08.1980, BGBl. I 1310).','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2405','Aufgeteilter Anteil am Gebäudeeigentum','Hierbei wurde der Anteil an einem Gebäudeeigentum nochmals unterteilt. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2104','Fischereirecht','Fischereirecht ist die Befugnis, in einem Binnengewässer (See, Teich, Fluß, Bach) Fische, Krebse und andere nutzbare Wassertiere (z.B. Muscheln, Frösche), die nicht Gegenstand des Jagdrechts sind, zu hegen und sich anzueignen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2102','Untererbbaurecht','Untererbbaurecht ist das Erbbaurecht an einem Erbbaurecht. Hier ist der Belastungsgegenstand nicht das Grundstück, sondern das auf diesem lastenden Erbbaurecht.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1403','Aufgeteilter Anteil am ungetrennten Hofraum','Hier wurde der Anteil an ungeteiltem Hofraum nochmals unterteilt. Die vorgenommene Grundbucheintragung deutet auf eine Untergemeinschaft innerhalb der Gesamtgemeinschaft hin. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2302','Wohnungs-/Teiluntererbbaurecht','Wohnungs-/Teiluntererbbaurecht ist die Aufteilung eines Untererbbaurechts analog Par. 30 WEG.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2301','Wohnungs-/Teilerbbaurecht','Wohnungs-/Teilerbaurechte können nach Par. 30 WEG unter Anwendung der Par. 3, 8 WEG begründet werden, wobei an die Stelle des Miteigentums am Grundstück die Mitberechtigung nach Bruchteilen an einem Erbbaurecht tritt, mit welchem das Sondereigentum an der Wohnung bzw. den nicht zu Wohnzwecken dienenden Räumen verbunden wird.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('5202','Anliegergraben','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('6101','Nicht gebuchtes Fischereirecht','Das nicht gebuchte Fischereirecht wird nach Wasserrecht im Fischwasserkataster nachgewiesen und ist im Grundbuch nicht gebucht.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2204','Aufgeteiltes Recht, Körperschaft','Diese Buchungsart ist die Zusammenfassung aller auf den Grundbuchblättern der herrschenden Grundstücke gebuchten Nutzanteile an einer Körperschaft. Es handelt sich hier um eine Buchungsart für das Fiktive Blatt. Eine Körperschaft besteht aus einem Verband von Mitgliedern, deren Mitgliedschaft an landesrechtliche (meistens altrechtliche ) und persönliche Merkmale gebunden ist; die Mitglieder haben das Recht zur Nutzung des Grundstücks in einem bestimmten Umfang (z.B. Körperschaftswaldungen).','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1302','Miteigentum Par. 3 Abs. 4 GBO','Ein Miteigentum nach Par. 3 Abs. 4 der Grundbuchordnung (GBO) ist ein Miteigentum an einem dienenden Grundstück. Ist das Grundstück im wirtschaftlichen Sinn als Zubehör mehrerer anderer Grundstücke anzusehen und steht es im Miteigentum dieser Grundstücke (Bruchteilseigentum nach Par. 1008 ff des Bürgerlichen Gesetzbuchs (BGB), muss das Grundstück nicht in einem separaten Grundbuch geführt werden. Vielmehr wird das dienende Grundstück in ideellen Miteigentumsanteilen auf den Grundbuchblättern der herrschenden Grundstücke gebucht.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1102','Aufgeteiltes Grundstück Par. 3 Abs. 4 GBO','Ein aufgeteiltes Grundstück nach Par. 3 Abs. 4 GBO ist die Zusammenfassung aller dienenden Miteigentumsanteile eines Grundstücks (Miteigentumsanteil nach § 3 Abs. 4 GBO). Es handelt sich daher um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1402','Aufgeteilter Anteil Miteigentum Par. 3 Abs. 4 GBO','Hier wurde der Miteigentumsanteil nach Par. 3 (4) GBO nochmals unterteilt. Die vorgenommene Grundbucheintragung deutet auf eine Untergemeinschaft innerhalb der Gesamtgemeinschaft hin. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2404','Aufgeteiltes anteiliges Recht, Körperschaft','Hierbei wurde der Anteil an einem anteiligem Recht Körperschaft nochmals unterteilt. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Ungetrennter Hofraum','Zu einem ungetrennten Hofraum gehören Grundstücke, die zwar in ihren Außengrenzen, nicht aber bezüglich der daran bestehenden Anteile vermessen und katastermäßig erfasst sind. Im Grundbuch werden die nicht ausgemessenen , einzelnen Grundstücksflächen als ''Anteil an einem ungetrennten Hofraum'' ausgewiesen. Bis zur Vermessung der einzelnen Grundstücksflächen und Aufnahme in das Liegenschaftsbuch gilt das Gebäudesteuerbuch als amtliches Verzeichnis nach Par. 2 Abs. 2 GBO. Diese Nummer des Gebäudesteuerbuchs ist bis zur Vermessung im Grundbuch eingetragen. Für ungetrennte Hofräume existieren zwei miteinander korrespondierende Eintragungen:In einem Grundbuchblatt sind alle ungetrennten Hofräume eines Grundbuchbezirks verzeichnet. Im zweiten Grundbuchblatt ist im Bestandsverzeichnis dann der ''Anteil an einem bestimmten ungetrennten Hofraum'' eingetragen.Nach der katasterlichen Erfassung wird die bisherige Eintragung in beiden Grundbüchern gelöscht und das vermessenene Grundstück als ''normales Grundstück'' gebucht.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('4100','Stockwerkseigentum','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2402','Aufgeteilter Anteil Wohnungs-/Teiluntererbbaurecht','Hierbei wurde der Anteil an einem Wohnungs-/Teiluntererbbaurecht nochmals unterteilt. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2502','Anteil am Wohnungs-/Teiluntererbbaurechtsanteil','Hier wird der Anteil an dem Wohnungs-/Teiluntererbbaurechtsanteil im Grundbuch eingetragen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2403','Aufgeteilter Erbbaurechtsanteil Par. 3 Abs. 4 GBO','Hierbei wurde der Anteil an einem Erbbaurechtsanteil nochmals unterteilt. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2503','Anteil am Erbbaurechtsanteil Par. 3 Abs. 4 GBO','Hier wird der Anteil an dem Erbbaurechtsanteil im Grundbuch eingetragen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1401','Aufgeteilter Anteil Wohnungs-/Teileigentum','Hier wurde der mit dem Sondereigentum verbundene Miteigentumsanteil (Wohnungs-/Teileigentum) nochmals unterteilt. Die vorgenommene Grundbucheintragung deutet auf eine Untergemeinschaft innerhalb der Gesamtgemeinschaft hin. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2202','Aufgeteiltes Untererbbaurecht WEG','Diese Buchungsart ist die Zusammenfassung aller Anteile eines Untererbbaurechts, die auf mehreren Grundbuchblättern gebucht sind. Es handelt sich hier um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5200','Anliegerflurstück','Ein Flurstück dessen Teilflächen den anliegenden Flurstücken zugerechnet wird.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2103','Gebäudeeigentum','Das Gebäudeeigentum an einem Grundstück als Ganzes zur Errichtung und Nutzung eines Gebäudes.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2203','Aufgeteiltes Recht Par. 3 Abs. 4 GBO','Diese Buchungsart ist die Zusammenfassung aller dienenden Miteigentumsanteile eines Erbbaurechts. Es handelt sich hier um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2107','Realgewerberecht','Hierbei handelt es sich um die frei veräußerliche und vedrerbliche Befugnis zum Betrieb eines bestimmten Gewerbes, die mit dem Besitz einer Liegenschaft verbunden sein kann aber nicht zwingend an ein bestimmtes Grundstücks gebunden sein muss. Die nähere Bezeichnung des Nutzungsrechts ergibt sich aus dem Attribut ''Ergänzung der Buchung''.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2106','Nutzungsrecht','Hierunter sind alle Nutzungsrechte zu verstehen, die im Bestandsverzeichnis eingetragen werden, unabhängig von ihrer öffentlich- oder privatrechtlichen Natur. Die nähere Bezeichnung des Nutzungsrechts ergibt sich aus dem Attribut ''Ergänzung der Buchung''.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2303','Erbbaurechtsanteil Par. 3 Abs. 4 GBO','Ein Erbbaurechtsanteil nach Par. 3 Abs. 4 der Grundbuchordnung (GBO) ist ein Miteigentum an einem dienenden Erbbaurecht.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2205','Aufgeteiltes Gebäudeeigentum','Diese Buchungsart ist die Zusammenfassung aller Anteile eines Gebäudeeigentums, die auf mehreren Grundbuchblättern gebucht sind. Es handelt sich hier um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2501','Anteil am Wohnungs-/Teilerbbaurechtsanteil','Hier wird der Anteil an dem Wohnungs-/Teilerbbaurechtsanteil im Grundbuch eingetragen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2304','Anteiliges Recht, Körperschaft','Eintragung eines Anteils an dem Recht - Körperschaft nach Par. 9 GBO im Grundbuch des jeweils herrschenden Grundstückes, dabei besteht die Körperschaft aus einem Verband von Mitgliedern, deren Mitgliedschaft an gebietliche und persönliche Merkmale geknüpft ist (z. B. Körperschaftswaldungen).','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2504','Anteil am anteiligen Recht, Körperschaft','Hier wird der Anteil an dem anteiligen Recht Körperschaft im Grundbuch eingetragen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2201','Aufgeteiltes Erbbaurecht WEG','Diese Buchungsart ist die Zusammenfassung aller Anteile eines Erbbaurechts, die auf mehreren Grundbuchblättern gebucht sind. Es handelt sich hier um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2108','Gemeinderecht','Gemeinderecht ist das Recht zur Nutzung eines gemeinschftlichen Grundstücks. Die näheren Angaben zu diesem Recht sind in privatrechtlichen Verträgen enthalten.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2401','Aufgeteilter Anteil Wohnungs-/Teilerbbaurecht','Hierbei wurde der Anteil an einem Wohnungs-/Teilerbbaurecht nochmals unterteilt. Es handelt sich um eine Buchungsart für das Fiktive Blatt.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Grundstück','Das Grundstück ist ein räumlich abgegrenzter Teil der Erdoberfläche, der auf einem besonderen Blatt, dem Grundbuchblatt, für sich allein oder auf einem gemeinschaftlichen Grundbuchblatt unter einer eindeutigen Nummer des Bestandsverzeichnisses eingetragen ist (Grundstück im Rechtssinn). Das Grundstück besteht aus einem oder mehreren Flurstücken.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1303','Anteil am ungetrennten Hofraum','Hierbei handelt es sich um die Buchung des Anteils am ungetrennten Hofraum.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1501','Anteil an Wohnungs-/Teileigentumsanteil','Hier wird der Anteil an dem Wohnungs-/Teileigentumsanteil im Grundbuch eingetragen.','buchungsart','ax_buchungsstelle');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1301','Wohnungs-/Teileigentum','Das Wohnungseigentum kann nach Par. 3 Wohnungseigentumsgesetz (WEG) durch Vertrag der Miteigentümer oder nach Par. 8 WEG durch Erklärung des Eigentümers begründet werden.Das entstehende Wohnungseigentum (Teileigentum) ist echtes Eigentum bürgerlichen Rechts in Form einer rechtlichen Verbindung von Miteigentum an Grundstück und Gebäude mit Sondereigentum an einer Wohnung bzw. Teileigentum an nicht zu Wohnzwecken dienenden Räumen.','buchungsart','ax_buchungsstelle');
