INSERT INTO alkis_elemente(name,kennung,definition,abgeleitet_aus,modellart,type) VALUES ('ax_fortfuehrungsnummer','15004','''AX_Fortführungsnummer'' enthält die Bestandteile zur Ableitung der Nummer des Fortführungsnachweises.','{}','DLKM','Datentyp');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','CharacterString','LFD','Diese Attributart enthält die laufende Nummer des Fortführungsnachweises.','ax_fortfuehrungsnummer','DLKM','laufendenummer');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','CharacterString','LAN','Diese Attributart enthält den Schlüssel des Bundeslandes.','ax_fortfuehrungsnummer','DLKM','land');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','CharacterString','GEM','Diese Attributart enthält den Schlüssel der Gemarkung.','ax_fortfuehrungsnummer','DLKM','gemarkungsnummer');
INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_bergbaubetrieb','DLKM','41004','REO','[E] ''Bergbaubetrieb'' ist eine Fläche, die für die Förderung des Abbaugutes unter Tage genutzt wird.','{"ax_tatsaechlichenutzung"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NAM','''Name'' ist der Eigenname von ''Bergbaubetrieb''.','ax_bergbaubetrieb','DLKM','name');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Abbaugut_Bergbaubetrieb','AGT','''Abbaugut'' gibt an, welches Material abgebaut wird.','ax_bergbaubetrieb','DLKM','abbaugut');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5006','Flussspat','''Flussspat'' ist ein Abbaugut, das aus Calciumfluorid besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5005','Kalkspat','''Kalkspat'' ist ein weißes oder hell gefärbtes Abbaugut (Calciumcarbonat).','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2021','Talkschiefer, Speckstein','''Talkschiefer, Speckstein'' ist ein farbloses bis graugrünes, sich fettig anfühlendes Abbaugut, das aus dem weichen Mineral Talk besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3007','Wismut, Kobalt, Nickel','''Wismut, Kobalt, Nickel'' werden als Erze abgebaut und durch spezielle Verfahren gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2003','Metamorpher Schiefer','''Metamorpher Schiefer'' ist ein Abbaugut, dessen ursprüngliche Zusammensetzung und Struktur durch Wärme und Druck innerhalb der Erdkruste verändert worden ist.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3011','Edelmetallerze','''Edelmetallerze'' ist das Abbaugut, aus dem Edelmetalle (z. B. Gold, Silber) gewonnen werden.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3010','Antimon','''Antimon'' wird als Antimonerz abgebaut und durch spezielle Verfahren gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5000','Industrieminerale, Salze','''Industrieminerale, Salze'' bedeutet, dass die in der Natur vorkommenden Mineralien abgebaut werden.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5004','Kalisalz','''Kalisalz'' ist ein Abbaugut, das aus Salzstöcken gewonnen wird und aus Chloriden und Sulfaten besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1001','Ton','''Ton'' ist ein Abbaugut, das aus gelblichem bis grauem Lockergestein besteht und durch Verwitterung älterer Gesteine entsteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3004','Blei','''Blei'' wird als Bleierz abgebaut und durch spezielle Verfahren gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Erden, Lockergestein','''Erden, Lockergestein'' bedeutet, dass feinkörnige Gesteine abgebaut werden.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4020','Kohle','''Kohle'' ist ein Abbaugut, das durch Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5002','Anhydritstein','''Anhydritstein'' ist ein Abbaugut, das aus wasserfreiem Gips besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4030','Ölschiefer','''Ölschiefer'' ist ein Abbaugut, das aus dunklem, bitumenhaltigem, tonigem Gestein besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3008','Uran','''Uran'' wird als Uranerz abgebaut und durch spezielle Verfahren gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3006','Zinn','''Zinn'' wird als Zinnerz abgebaut und durch spezielle Verfahren gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3001','Eisen','''Eisen'' wird als Eisenerz abgebaut und durch Verhüttung gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3002','Buntmetallerze','''Buntmetallerze'' ist das Abbaugut, das alle Nichteisenmetallerze als Sammelbegriff umfasst.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1007','Kalk, Kalktuff, Kreide','''Kalk, Kalktuff, Kreide'' ist ein Abbaugut, das aus erdigem weißen Kalkstein besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3009','Mangan','''Mangan'' wird als Manganerz abgebaut und durch spezielle Verfahren gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5003','Steinsalz','''Steinsalz'' ist ein Abbaugut, das aus Salzstöcken gewonnen wird und aus Natriumchlorid besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4021','Braunkohle','''Braunkohle'' ist ein Abbaugut, das durch einen bestimmten Grad von Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3005','Zink','''Zink'' wird als Zinkerz abgebaut und durch spezielle Verfahren gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3003','Kupfer','''Kupfer'' wird als Kupfererz abgebaut und durch Verhüttung gewonnen.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5011','Graphit','''Graphit'' ist ein bleigraues, weiches, metallglänzendes Abbaugut, das aus fast reinem Kohlenstoff besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2013','Basalt, Diabas','''Basalt, Diabas'' ist ein Abbaugut, das aus basischem Ergussgestein besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2002','Schiefer, Dachschiefer','''Schiefer, Dachschiefer'' ist ein toniges Abbaugut, das in dünne ebene Platten spaltbar ist.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4000','Treib- und Brennstoffe','''Treib- und Brennstoffe'' bedeutet, dass die in der Natur vorkommenden brennbaren organischen und anorganischen Substanzen abgebaut oder gewonnen werden.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5007','Schwerspat','''Schwerspat'' ist ein formenreiches, rhombisches weißes bis farbiges Abbaugut.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2005','Kalkstein','''Kalkstein'' ist ein Abbaugut, das als weit verbreitetes Sedimentgestein überwiegend aus Calciumcarbonat besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2006','Dolomitstein','''Dolomitstein'' ist ein Abbaugut, das überwiegend aus calcium- und magnesiumhaltigen Mineralien besteht.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4022','Steinkohle','''Steinkohle'' ist ein Abbaugut, das durch vollständige Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2000','Steine, Gestein, Festgestein','''Steine, Gestein, Festgestein'' bedeutet, dass grobkörnige oder feste Gesteine abgebaut werden.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5001','Gipsstein','''Gipsstein'' ist ein natürliches Abbaugut.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3000','Erze','''Erze'' bedeutet, dass die in der Natur vorkommenden, metallhaltigen Mineralien und Mineralgemische abgebaut oder gespeichert werden.','abbaugut','ax_bergbaubetrieb');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BEZ','''Bezeichnung'' ist die von einer Fachstelle vergebene Kurzbezeichnung.','ax_bergbaubetrieb','DLKM','bezeichnung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Zustand_Bergbaubetrieb','ZUS','''Zustand'' beschreibt die Betriebsbereitschaft von ''Bergbaubetrieb''.','ax_bergbaubetrieb','DLKM','zustand');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('8000','Erweiterung, Neuansiedlung','zustand','ax_bergbaubetrieb');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2100','Außer Betrieb, stillgelegt, verlassen','''Außer Betrieb, stillgelegt; verlassen'' bedeutet, dass sich der Bergbaubetrieb nicht mehr in regelmäßiger, der Bestimmung entsprechenden Nutzung befindet.','zustand','ax_bergbaubetrieb');
INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_strassenverkehr','DLKM','42001','REO','[E] ''Straßenverkehr'' umfasst alle für die bauliche Anlage Straße erforderlichen sowie dem Stra- ßenverkehr dienenden bebauten und unbebauten Flächen.','{"ax_tatsaechlichenutzung"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Funktion_Strasse','FKT','''Funktion'' beschreibt die verkehrliche Nutzung von ''Straßenverkehr''.','ax_strassenverkehr','DLKM','funktion');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('2311','Gebäude- und Freifläche zu Verkehrsanlagen, Straße','funktion','ax_strassenverkehr');
