INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2322','Verkehrsbegleitfläche Bahnverkehr','''Verkehrsbegleitfläche Bahnverkehr'' bezeichnet eine bebaute oder unbebaute, an den Bahnkörper angrenzende Fläche, die dem Schienenverkehr dient.','funktion','ax_bahnverkehr');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NRB','''Nummer der Bahnstrecke'' ist die von der Bahn AG festgelegte Verschlüsselung der Bahnstrecke.','ax_bahnverkehr','DLKM','nummerderbahnstrecke');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','ZNM','''Zweitname'' ist der von der Lagebezeichnung abweichende Name von ''Bahnverkehr'' (z. B. ''Höllentalbahn'').','ax_bahnverkehr','DLKM','zweitname');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Lagebezeichnung','BEZ','''Bezeichnung'' ist die Angabe der Orte, in denen die Bahnlinie beginnt und endet (z. B. ''Bahnlinie Frankfurt - Würzburg).','ax_bahnverkehr','DLKM','bezeichnung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..*','AX_Bahnkategorie','BKT','''Bahnkategorie'' beschreibt die Art des Verkehrsmittels.','ax_bahnverkehr','DLKM','bahnkategorie');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Stadtbahn','''Stadtbahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem eine elektrisch betriebene Schienenbahn zur Personenbeförderung im öffentlichen Nahverkehr fährt. Sie kann sowohl oberals auch unterirdisch verlaufen.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1600','Magnetschwebebahn','''Magnetschwebebahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem räderlose Schienenfahrzeuge mit Hilfe von Magnetfeldern an oder auf einer Fahrschiene schwebend entlanggeführt werden.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1302','Standseilbahn','''Standseilbahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem eine Schienenbahn auf einer stark geneigten, meist kurzen und geraden Strecke verläuft. Mit Hilfe eines oder mehrerer Zugseile wird ein Schienenfahrzeug bergauf gezogen und gleichzeitig ein zweites bergab gelassen.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1201','Straßenbahn','''Straßenbahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem eine elektrisch betriebene Schienbahn zur Personenbeförderung fährt. Sie verläuft i. d. R. oberirdisch.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1102','Güterverkehr','''Güterverkehr'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem im Nah- und Fernverkehr ausschließlich Güter transportiert werden.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1400','Museumsbahn','''Museumsbahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem ausschließlich Touristen in alten, meist restaurierten Zügen befördert werden.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Eisenbahn','''Eisenbahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem im Nah- und Fernverkehr Personen befördert und Güter transportiert werden.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1202','U-Bahn','''U-Bahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem eine elektrisch betriebene Schienenbahn zur Personenbeförderung in Großstädten fährt. Sie verläuft i. d. R. unterirdisch.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1301','Zahnradbahn','''Zahnradbahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem eine Schienenbahn mittels Zahnradantrieb große Höhenunterschiede in stark geneigtem Gelände überwindet.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1300','Seilbahn, Bergbahn','''Seilbahn, Bergbahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, auf dem eine Schienenbahn große Höhenunterschiede überwindet.','bahnkategorie','ax_bahnverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1104','S-Bahn','''S-Bahn'' ist die Bezeichnung für einen schienengebundenen Verkehrsweg, der zur schnellen Personenbeförderung in Ballungsräumen dient und meist auf eigenen Gleisen verläuft.','bahnkategorie','ax_bahnverkehr');
