INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Hecke','''Hecke'' besteht aus einer Reihe dicht beieinander stehender, meist wildwachsender Sträucher.','bewuchs','ax_vegetationsmerkmal');
