INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1800','Reet','''Reet'' bezeichnet eine ständig oder zeitweise unter Wasser stehende und mit Reet bewachsene Fläche.','bewuchs','ax_vegetationsmerkmal');
