INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1260','Gebüsch','''Gebüsch'' beschreibt den Bewuchs einer Vegetationsfläche mit Holzpflanzen, deren Sprossen sich nahe der Bodenoberfläche verzweigen.','bewuchs','ax_vegetationsmerkmal');
