INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1300','Schneise','''Schneise'' ist eine künstlich angelegte Waldeinteilungslinie zur dauerhaften Begrenzung forstlicher Wirtschaftsflächen (räumliche Ordnung), die in der Regel geradlinig verläuft.','bewuchs','ax_vegetationsmerkmal');
