INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('8300','Kanal','''Kanal'' ist ein für die Schifffahrt angelegter, künstlicher Wasserlauf.','funktion','ax_fliessgewaesser');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('8410','Fleet','funktion','ax_fliessgewaesser');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('8220','Altarm','funktion','ax_fliessgewaesser');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('8230','Flussmündungstrichter','''Flussmündungstrichter'' ist der Bereich des Flusses im Übergang zum Meer. Er beginnt dort, wo die bis dahin etwa parallel verlaufenden Ufer des Flusses sich trichterförmig zur offenen See hin erweitern. Die Abgrenzungen der Flussmündungstrichter ergeben sich aus dem Bundeswasserstraßengesetz (meerseitig) und den Bekanntmachungen der Wasser- und Schifffahrtsverwaltung sowie höchst-richterlicher Rechtsprechung (binnenseitig).','funktion','ax_fliessgewaesser');
