INSERT INTO alkis_elemente(name,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_bauwerkimverkehrsbereich','53001','REO','[E] ''Bauwerk im Verkehrsbereich'' ist ein Bauwerk, das dem Verkehr dient.','{"ax_bauwerkeeinrichtungenundsonstigeangaben","ag_objekt"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NAM','''Name'' ist der Eigenname von ''Bauwerk im Verkehrsbereich''.','ax_bauwerkimverkehrsbereich','DLKM','name');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BEZ','''Bezeichnung'' ist die von einer Fachstelle vergebene Kennziffer von ''Bauwerk im Verkehrsbereich''.','ax_bauwerkimverkehrsbereich','DLKM','bezeichnung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Zustand_BauwerkImVerkehrsbereich','ZUS','''Zustand'' beschreibt die derzeitige Benutzbarkeit von ''Bauwerk im Verkehrsbereich''.','ax_bauwerkimverkehrsbereich','DLKM','zustand');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2100','Außer Betrieb, stillgelegt, verlassen','''Außer Betrieb, stillgelegt, verlassen'' bedeutet, dass sich ''Bauwerk im Verkehrsbereich'' nicht mehr in regelmäßiger, der Bestimmung entsprechenden Nutzung befindet.','zustand','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','AX_Bauwerksfunktion_BauwerkImVerkehrsbereich','BWF','''Bauwerksfunktion'' beschreibt die besondere Funktion oder Bauart von ''Bauwerk im Verkehrsbereich''.','ax_bauwerkimverkehrsbereich','DLKM','bauwerksfunktion');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1870','Tunnel, Unterführung','''Tunnel, Unterführung'' ist ein künstlich angelegtes unterirdisches Bauwerk, das im Verlauf von Verkehrswegen durch Bergmassive oder unter Flussläufen, Meerengen, städt. Bebauungen u. a. hindurchführt.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1840','Brückenpfeiler','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1804','Hängebrücke','''Hängebrücke'' ist eine Brücke, bei der das Tragwerk von Hängegurten (Kabel) an einem oder mehreren Pylonen gehalten wird.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1850','Brückenjoch','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1801','Mehrstöckige Brücke','''Mehrstöckige Brücke'' ist eine Brücke, die mit Verkehrswegen in mehreren Etagen ausgestattet ist.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1900','Durchfahrt','''Durchfahrt'' ist eine Stelle, an der mit Fahrzeugen durch ein Bauwerk (z.B. ein Turm, eine Mauer) hindurch gefahren werden kann.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1830','Hochbahn, Hochstraße','''Hochbahn, Hochstraße'' ist ein brückenartiges, aufgeständertes Verkehrsbauwerk.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1802','Bogenbrücke','''Bogenbrücke'' ist eine Brücke, bei der das Tragwerk aus Bögen besteht.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1808','Zugbrücke','''Zugbrücke'' ist eine Brücke, bei der das Tragwerk um eine waagerechte Achse hochgeklappt wird.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1845','Widerlager','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1820','Steg','''Steg'' ist ein Bauwerk, das Fußgängern den Übergang über ein Gewässer ermöglicht.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1805','Pontonbrücke','''Pontonbrücke'' ist eine Behelfsbrücke, die sich aus kastenförmigen Schwimmkörpern zusammensetzt.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1807','Hebebrücke','''Hebebrücke'' ist eine Brücke, bei der das Tragwerk an Seilen oder Ketten emporgehoben wird.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1810','Landebrücke','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1880','Schutzgalerie','''Schutzgalerie'' ist eine in der Regel arkadenartige Überbauung von Verkehrswegen, die zum Schutz gegen Lawinen, Schneeverwehungen und Steinschlägen sowie zum Schutz gegen Emission dient.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1806','Drehbrücke','''Drehbrücke'' ist eine Brücke, bei der sich das Tragwerk um einen senkrechten Zapfen (Königsstuhl) dreht.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('9999','Sonstiges','''Sonstiges'' bedeutet, dass die Bauwerksfunktion bekannt, aber nicht in der Attributwertliste aufgeführt ist.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1803','Fachwerkbrücke','''Fachwerkbrücke'' ist eine Brücke, bei der das Tragwerk aus starr zusammengesetzten Tragbalken (Holz oder Metall) besteht.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1800','Brücke','''Brücke'' ist ein Bauwerk zum Zweck der Überführung eines Verkehrsweges über einen anderen Verkehrsweg oder über ein Gewässer sowie zur Überführung über ein tieferliegendes Gelände.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1910','Anflugbefeuerung','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1890','Schleusenkammer','''Schleusenkammer'' ist eine Einrichtung zur Überführung von Wasserfahrzeugen zwischen Gewässern mit unterschiedlichen Wasserspiegelhöhen.','bauwerksfunktion','ax_bauwerkimverkehrsbereich');
