INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1670','Nationalpark','''Nationalpark'' ist ein rechtsverbindlich festgesetztes einheitlich zu schützendes Gebiet, das großräumig und von besonderer Eigenart ist, im überwiegenden Teil die Voraussetzungen eines Naturschutzgebietes erfüllt und sich in einem vom Menschen nicht oder nur wenig beeinflussten Zustand befindet.','artderfestlegung','ax_schutzgebietnachnaturumweltoderbodenschutzrecht');
