INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1720','Stützmauer','''Stützmauer'' ist eine zum Stützen von Erdreich dienende Mauer.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
