INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1750','Denkmal, Denkstein, Standbild','''Denkmal, Denkstein, Standbild'' ist ein zum Gedenken errichtetes Bauwerk an eine Person, ein Ereignis oder eine plastische Darstellung.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1790','Spundwand','''Spundwand'' ist ein Sicherungsbauwerk (wasserdichte Wand) aus miteinander verbundenen schmalen, langen Holz-, Stahl- oder Stahlbetonbohlen zum Schutz gegen das Außenwasser. Die Bohlen werden horizontal hinter Pfählen (Bohlwand) oder vertikal als Spundwand eingebaut und meist rückwärtig verankert.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
