INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1620','Treppe','''Treppe'' ist ein stufenförmiges Bauwerk zur Überwindung von Höhenunterschieden.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1703','Mauermitte','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1740','Zaun','''Zaun'' ist eine Abgrenzung oder Einfriedung aus Holz- oder Metallstäben oder aus Draht bzw. Drahtgeflecht.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1780','Brunnen','''Brunnen'' ist eine Anlage zur Gewinnung von Grundwasser bzw. ein architektonisch ausgestaltetes Bauwerk mit Becken zum Auffangen von Wasser.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
