INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1640','Kellereingang','''Kellereingang'' ist der Eingang zu einem unterirdischen Vorratsraum außerhalb von Gebäuden.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1702','Mauerkante, links','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1781','Brunnen (Trinkwasserversorgung)','''Brunnen (Trinkwasserversorgung)'' bedeutet, dass in dem Brunnen ausschließlich Trinkwasser gewonnen wird.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1700','Mauer','''Mauer'' ist ein freistehendes, langgestrecktes Bauwerk, das aus Natur- bzw. Kunststeinen oder anderen Materialien besteht.','bauwerksfunktion','ax_sonstigesbauwerkodersonstigeeinrichtung');
