INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','AX_ArchaeologischerTyp_HistorischesBauwerkOderHistorischeEinrichtung','ATP','''Archäologischer Typ'' beschreibt die Art von ''Historisches Bauwerk oder historische Einrichtung''.','ax_historischesbauwerkoderhistorischeeinrichtung','DLKM','archaeologischertyp');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1100','Wasserleitung','''Wasserleitung'' ist ein meist offenes System von Gräben, Kunstgräben und Kanälen, in dem Wasser transportiert wird.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1430','Lager','''Lager'' ist die Bezeichnung für ein befestigtes Truppenlager in der Römer- oder in der Neuzeit (z.B. bei Belagerungen im 30 jährigen Krieg).','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1210','Wachtturm (römisch), Warte','''Wachtturm (römisch), Warte'' ist ein allein oder in Verbindung mit einem Befestigungssystem (Limes) stehender Beobachtungsturm.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1010','Großsteingrab (Dolmen), Hünenbett','''Großsteingrab (Dolmen), Hünenbett'' ist ein Grab mit Steineinbau, d. h. es ist ein aus großen Steinen (z.B. Findlingen) errichteter Grabbau.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('9999','Sonstiges','''Sonstiges'' bedeutet, dass der archäologische Typ bekannt, aber nicht in der Attributwertliste aufgeführt ist','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1420','Schanze','''Schanze'' ist eine mittelalterliche oder neuzeitliche, in der Regel geschlossene, quadratische, rechteckige oder sternförmige Wallanlage mit Außengraben.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1510','Stadtmauer','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Grab','''Grab'' ist eine künstlich geschaffene Bestattungsstätte unter, auf oder über der Erdoberfläche.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Befestigung (Wall, Graben)','''Befestigung (Wall, Graben)'' ist ein aus Erde aufgeschütteter Grenz-, Schutz- oder Stadtwall. Zu der Befestigung (Wall) zählen auch Limes und Landwehr.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1300','Steinmal','''Steinmal'' ist eine kultische oder rechtliche Kennzeichnung, bestehend aus einzelnen oder Gruppen von Steinen.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1020','Grabhügel (Hügelgrab)','''Grabhügel (Hügelgrab)'' ist ein meist runder oder ovaler Hügel, der über einer ur- oder frühgeschichtlichen Bestattung aus Erde aufgeschüttet oder aus Plaggen aufgeschichtet wurde.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1110','Aquädukt','''Aquädukt'' ist ein brückenartiges Steinbauwerk zur Überführung von Freispiegel-Wasserleitungen mit natürlichem Gefälle über Täler oder andere Bodenunebenheiten.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1500','Historische Mauer','''Historische Mauer'' ist eine Mauer mit kulturgeschichtlicher Bedeutung.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1410','Burg (Fliehburg, Ringwall)','''Burg (Fliehburg, Ringwall)'' ist eine ur- oder frühgeschichtliche runde, ovale oder an Gegebenheiten des Geländes (Böschungskanten) angepasste Befestigungsanlage, die aus einem Erdwall mit oder ohne Holzeinbauten besteht.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1400','Befestigung (Burgruine)','''Befestigung (Burgruine)'' ist eine künstliche Anlage zur Sicherung von Leben und Gut.','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1520','Sonstige historische Mauer','archaeologischertyp','ax_historischesbauwerkoderhistorischeeinrichtung');
