INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1150','Gesundheit, Kur','''Gesundheit, Kur'' bezeichnet eine Fläche auf der vorwiegend Gebäude des Gesundheitswesens stehen, z.B. Krankenhäuser, Heil- und Pflegeanstalten.','funktion','ax_flaechebesondererfunktionalerpraegung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1170','Sicherheit und Ordnung','''Sicherheit und Ordnung'' bezeichnet eine Fläche auf der vorwiegend Anlagen und Gebäude der Polizei, der Bundeswehr, der Feuerwehr und der Justizvollzugsbehörden stehen.','funktion','ax_flaechebesondererfunktionalerpraegung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1130','Kultur','''Kultur'' bezeichnet eine Fläche auf der vorwiegend Anlagen und Gebäude für kulturelle Zwecke, z.B. Konzert- und Museumsgebäude, Bibliotheken, Theater, Schlösser und Burgen sowie Rundfunk- und Fernsehgebäude stehen.','funktion','ax_flaechebesondererfunktionalerpraegung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1120','Bildung und Forschung','''Bildung und Forschung'' bezeichnet eine Fläche, auf der vorwiegend Gebäude stehen, in denen geistige, kulturelle und soziale Fähigkeiten vermittelt werden und/oder wissenschaftliche Forschung betrieben wird (z.B. Schulen, Universitäten, Forschungsinstitute).','funktion','ax_flaechebesondererfunktionalerpraegung');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1300','Historische Anlage','funktion','ax_flaechebesondererfunktionalerpraegung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1110','Verwaltung','''Verwaltung'' bezeichnet eine Fläche auf der vorwiegend Gebäude der öffentlichen Verwaltung, z. B. Rathaus, Gericht, Kreisverwaltung stehen.','funktion','ax_flaechebesondererfunktionalerpraegung');
